INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142722, 'Quazepam', 'Nefazodone', 'Moderate', 'Nefazodone, when administered with alprazolam or triazolam, has been reported to increase their half-life, area under the plasma concentration-time curve, and peak plasma concentrations. Increased sedation and altered psychomotor performance have resulted. The mechanism appears to be inhibition of the CYP450 3A4 enzyme by nefazodone. This interaction may occur with all benzodiazepines that undergo oxidative metabolism, although it has been reported only with alprazolam and triazolam.', NULL, 'Patients receiving this combination should be monitored for excessive and prolonged sedation.', NULL, 'Patients receiving this combination should be monitored for excessive and prolonged sedation. It may be necessary to adjust benzodiazepine dosage when nefazodone is started or stopped. Lorazepam, oxazepam, and temazepam undergo conjugative metabolism and may be suitable alternatives.', 'Metabolism', 'Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone', 'Thiopental, Ethchlorvynol, Dichloralphenazone, Nitrazepam', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142885/', '[1] Barbhaiya RH, Shukla UA, Kroboth PD, Greene DS "Coadministration of nefazodone and benzodiazepines: 2. a pharmacokinetic interaction study with triazolam." J Clin Psychopharmacol 15 (1995): 320-6[2] Kroboth PD, Mcauley JW, Derry CL "Time-dependent sensitization to triazolam? An observation in three studies." J Clin Psychopharmacol 15 (1995): 192-6[3] Riesenman C "Antidepressant drug interactions and the cytochrome p450 system: a critical appraisal." Pharmacotherapy 15