INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142732, 'Regorafenib', 'Nefazodone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of regorafenib and decrease the plasma concentrations of its active metabolites.', NULL, 'Concomitant use of regorafenib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of regorafenib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of regorafenib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Maprotiline, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, Trimipramine, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142895/', '[1] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142733, 'Remdesivir', 'Nefazodone', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Vortioxetine, Levomilnacipran, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142896/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142734, 'Remifentanil', 'Nefazodone', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate 