INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142741, 'Riociguat', 'Nefazodone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 or moderate to potent inhibitors of P-glycoprotein (P-gp) or breast cancer resistant protein (BCRP) may increase the plasma concentrations of riociguat, which is partially metabolized by CYP450 3A and also a substrate of the P-gp and BCRP efflux transporters. Increased levels of riociguat may increase the risk for hypotension.', NULL, 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors.', NULL, 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors. Patients should be monitored for signs and symptoms of hypotension, and the dosage of riociguat adjusted if necessary.', 'Metabolism', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142904/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
