INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142982, 'Caspofungin', 'Nelfinavir', 'Major', 'Coadministration with inducers or mixed inducer/inhibitors of certain metabolic pathways may produce clinically meaningful reductions in the plasma concentrations of caspofungin. This assumption is based on results from regression analyses of patient pharmacokinetic data derived from a small number of patients who were administered the drugs efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone, or carbamazepine prior to and/or concurrently with caspofungin. There are no data from formal drug interaction studies to evaluate these regression analyses, and it is not known which metabolic pathway involved in caspofungin clearance may be inducible.', NULL, 'The manufacturer of caspofungin states that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', NULL, 'The manufacturer of caspofungin states that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Micafungin, Amphotericin B, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143145/', '[1] "Product Information. Cancidas (caspofungin)" Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (142983, 'Cerivastatin', 'Nelfinavir', 'Major', 'Protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors. The mechanism is PI inhibition of CYP450 3A4 metabolism. High levels of HMG-CoA reductase inhibitory activity in plasma are associated with an increased risk of musculoskeletal toxicity, including myopathy and rhabdomyolysis, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'Simvastatin or lovastatin are considered contraindicated in patients treated with tipranavir and/or ritonavir due to the potential for severe interaction.', NULL, 'Simvastatin or lovastatin are considered contraindicated in patients treated with tipranavir and/or ritonavir due to the potential for severe interaction. Red yeast rice (which contains lovastatin) should also be avoided in patients treated with PIs. Fluvastatin and pravastatin are probably safer alternatives, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, 