INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143018, 'Flibanserin', 'Nelfinavir', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143181/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143019, 'Fluconazole', 'Nelfinavir', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', NULL, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143182/', '[1] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998): 300-4[2] Assan R, Fredj G, Larger E, Feutren G, Bismuth H "FK 506/fluconazole interaction enhances FK 506 nephrotoxicity." Diabete Metab 20 (1994): 49-52[3] Ulivelli M, Rubegni P, Nuti D, Bartalini S, Giannini F, Rossi S "Clinical evidence of fluconazole-induced carbamazepine toxicity." J Neurol 251 (2004): 622-3[4] "Product Information. Diflucan (fluconazole)." Roerig Division, New York, NY.[5] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000): 49-75[6] Canafax DM, Graves NM, Hilligoss DM, et al "Interaction between cyclosporine and fluconazole in renal allograft recipients." Transplantation 51 (1991): 1014-8[7] Hilbert J, Messig M, Kuye O, Friedman H "Evaluation of interaction between fluconazole and an oral contraceptive in healthy women." Obstet Gynecol 98 (2001): 218-23[8] Finch CK, Green CA, Self TH "Fluconazole-carbamazepine interaction." South Med J 95 (2002): 1099-100[9] Torregrosa V, De la Torre M, Campistol JM, et al "Interaction of fluconazole with ciclosporin A." Nephron 60 (1992): 125-6[10] Tsouli S, Maranis S, Kyritsis AP "Fluconazole-carbamazepine interaction in a patient with bipolar disorder." Psychiatry Clin Neurosci 65 (2011): 112[11] Barbara JA, Clarkson AR, LaBrooy J, et al "Candida albicans arthritis in a renal allograft recipient with an interaction between cyclosporin and fluconazole." Nephrol Dial Transplant 8 (1993): 263-6[12] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[13] Sugar AM, Saunders C, Idelson BA, Bernard DB "Interaction of fluconazole and cyclosporine." Ann Intern Med 110 (1989): 844[14] Osowski CL, Dix SP, Lin LS, Mullins RE, Geller RB, Wingard JR "Evaluation of the drug interaction between intravenous high-dose fluconazole and cyclosporine or tacrolimus in bone marrow transplant patients." Transplantation 61 (1996): 1268-72[15] Baciewicz AM, Baciewicz FA, Jr "Ketoconazole and fluconazole drug interactions." Arch Intern Med 153 (1993): 1970-6[16] Bedford TA, Rowbotham DJ "Cisapride: drug interactions of clinical significance." Drug Saf 15 (1996): 167-75[17] Lopez-Gil JA "Fluconazole-cyclosporin interaction: a dose-dependent effect?" Ann Pharmacother 27 (1993): 427-30[18] Nair DR, Morris HH "Potential fluconazole-induced carbamazepine toxicity." Ann Pharmacother 33 (1999): 790-2', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143020, 'Fludrocortisone', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of corticosteroids, which are primarily metabolized by the isoenzyme. The interaction has been reported with potent inhibitors such as clarithromycin, erythromycin, itraconazole, nefazodone, cobicistat, and ritonavir during concomitant use of various corticosteroids, including inhaled, nasal, and ophthalmic formulations. Systemic corticosteroid adverse effects may occur following intensive or long-term continuous ophthalmic corticosteroid therapy. Cushing''s syndrome and adrenal insufficiency have been attributed to the interaction.', NULL, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors.', NULL, 'The possibility of increased corticosteroid effects should be considered during coadministration with potent and moderate CYP450 3A4 inhibitors. When indicated for intranasal or inhalational use, alternative corticosteroids such as beclomethasone, which is less dependent on CYP450 3A4 metabolism, should be considered, particularly if long term treatment is required. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with a potent CYP450 3A4 inhibitor, a progressive dosage reduction may be required over a longer period if the corticosteroid is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143183/', '[1] Edsbacker S, Andersson T "Pharmacokinetics of budesonide (Entocort EC) capsules for Crohn''s disease." Clin Pharmacokinet 43 (2004): 803-21[2] Finkenbine RD, Frye MD "Case of psychosis due to prednisone-clarithromycin interaction." Gen Hosp Psychiat 20 (1998): 325-6[3] Soldatos G, Sztal-Mazer S, Woolley I, Stockigt J "Exogenous glucocorticoid excess as a result of ritonavir-fluticasone interaction." Intern Med J 35 (2005): 67-8[4] Kandrotas RJ, Slaughter RL, Brass C, Jusko WJ "Ketoconazole effects on methylprednisolone disposition and their joint suppression of endogenous cortisol." Clin Pharmacol Ther 42 (1987): 465-70[5] Raaska K, Niemi M, Neuvonen M, Neuvonen PJ, Kivisto KT "Plasma concentrations of inhaled budesonide and its effects on plasma cortisol are increased by the cytochrome P4503A4 inhibitor itraconazole." Clin Pharmacol Ther 72 (2002): 362-369[6] Ulrich B, Frey FJ, Speck RF, Frey BM "Pharmacokinetics/pharmacodynamics of ketoconazole-prednisolone interaction." J Pharmacol Exp Ther 260 (1992): 487-90[7] Zurcher RM, Frey BM, Frey FJ "Impact of ketoconazole on the metabolism of prednisolone." Clin Pharmacol Ther 45 (1989): 366-72[8] Busse KH, Formentini E, Alfaro RM, Kovacs JA, Penzak SR "Influence of antiretroviral drugs on the pharmacokinetics of prednisolone in HIV-infected individuals." J Acquir Immune Defic Syndr 48 (2008): 561-6[9] Yamashita SK, Ludwig EA, Middleton E Jr, Jusko WJ "Lack of pharmacokinetic and pharmacodynamic