INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143044, 'Halofantrine', 'Nelfinavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', NULL, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143207/', '[1] Charbit B, Becquemont L, Lepere B, Peytavin G, Funck-Bretano C "Pharmacokinetic and pharmacodynamic interaction between grapefruit juice and halofantrine." Clin Pharmacol Ther 72 (2002): 514-23[2] Giao PT, de Vries PJ "Pharmacokinetic interactions of antimalarial agents." Clin Pharmacokinet 40 (2001): 343-73[3] "Product Information. Halfan (halofantrine)." GlaxoSmithKline, Philadelphia, PA.[4] Abernethy DR, Wesche DL, Barbey JT, et al. "Stereoselective halofantrine disposition and effect: concentration-related QTc prolongation." Br J Clin Pharmacol 51 (2001): 231-7[5] Baune B, Flinois JP, Furlan V, et al. "Halofantrine metabolism in microsomes in man: major role of CYP 3A4 and CYP 3A5." J Pharm Pharmacol 51 (1999): 419-26[6] Giao PT, de Vries PJ "Pharmacokinetic interactions of antimalarial agents." Clin Pharmacokinet 40 (2001): 343-73[7] "Product Information. Halfan (halofantrine)." GlaxoSmithKline, Philadelphia, PA.[8] Abernethy DR, Wesche DL, Barbey JT, et al. "Stereoselective halofantrine disposition and effect: concentration-related QTc prolongation." Br J Clin Pharmacol 51 (2001): 231-7[9] Charbit B, Becquemont L, Lepere B, Peytavin G, Funck-Bretano C "Pharmacokinetic and pharmacodynamic interaction between grapefruit juice and halofantrine." Clin Pharmacol Ther 72 (2002): 514-23[10] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143045, 'Haloperidol', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nelfinavir.', NULL, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nelfinavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143208/', '[1] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143046, 'Hydroxychloroquine', 'Nelfinavir', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143209/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143047, 'Ibrutinib', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'The use of ibrutinib in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided, particularly those that are intended for chronic administration.', NULL, 'The use of ibrutinib in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided, particularly those that are intended for chronic administration. Some authorities recommend avoiding concomitant use of ibrutinib during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist and the CYP450 3A4 inhibitor is used short-term for 7 days or less, consider interrupting or delaying initiation of ibrutinib treatment until therapy with the inhibitor is complete. Patients should be closely monitored for signs of ibrutinib toxicity such as myelosuppression, bleeding, infection, and renal impairment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143210/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[6] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143048, 'Idarubicin', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143211/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143049, 'Idelalisib', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of idelalisib, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised if idelalisib is prescribed in combination with CYP450 3A4 and/or P-gp inhibitors. Pharmacologic response to idelalisib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the idelalisib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for idelalisib toxicity such as hepatotoxicity, diarrhea, colitis, intestinal perforation, pneumonitis, neutropenia, and thrombocytopenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143212/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363024);
