INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143051, 'Iloperidone', 'Nelfinavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of iloperidone, which is a substrate of the isoenzymes.', NULL, 'The dosage of iloperidone should be reduced by one-half when administered concomitantly with potent CYP450 3A4 inhibitors (e.', NULL, 'The dosage of iloperidone should be reduced by one-half when administered concomitantly with potent CYP450 3A4 inhibitors (e.g., itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, clarithromycin, telithromycin) and/or potent CYP450 2D6 inhibitors (e.g., dacomitinib, fluoxetine, paroxetine, quinidine). Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 or 2D6 inhibitor, the iloperidone dosage should be returned to the previous level.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143214/', '[1] "Product Information. Fanapt (iloperidone)." Vanda Pharmaceuticals Inc, Rockville, MD.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143052, 'Imatinib', 'Nelfinavir', 'Moderate', 'Imatinib is a substrate of the CYP450 3A4 isoenzyme as well as a potent competitive inhibitor of the CYP450 2C9, 2D6 and 3A4 isoenzymes. Some drugs that are known inhibitors of CYP450 3A4 are also metabolized by one or more of the isoenzymes inhibited by imatinib. Theoretically, coadministration of imatinib with those drugs may result in mutually elevated plasma drug concentrations due to competitive and noncompetitive inhibition of CYP450 activities.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin. In addition, clinical and laboratory monitoring for potentially increased pharmacologic effects of coadministered medications is recommended, especially those with a narrow therapeutic range.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143215/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363024);
