INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143055, 'Indinavir', 'Nelfinavir', 'Moderate', 'The combination of nelfinavir and indinavir has been reported to cause an 83% increase in nelfinavir area under the plasma concentration-time curve (AUC) and a 51% increase in the indinavir AUC. The mechanism is likely related to CYP450 3A4 hepatic enzyme inhibition. The safety of their concurrent use has not been established.', NULL, 'Patients should be monitored for adverse effects.', NULL, 'Patients should be monitored for adverse effects. Dose adjustments may be required; however, appropriate doses have not been established for this combination.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143218/', '[1] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[2] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[3] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8', 1767363024);
