INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143062, 'Irinotecan (liposomal)', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 is the isoenzyme responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', NULL, 'Concomitant use of irinotecan with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of irinotecan with potent CYP450 3A4 inhibitors should generally be avoided. Potent CYP450 3A4 inhibitors should be discontinued for at least one week before initiation of treatment with irinotecan.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143225/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Phansalker S, Desai AA, Bell D, et al "High-priority drug-drug interactions for use in electronic health records." J Am Med Inform Assoc 19 (2012): 735-43[3] Corona G, Vaccher E, Sandron S, et al. "Lopinavir-ritonavir dramatically affects the pharmacokinetics of irinotecan in HIV patients with Kaposi''s sarcoma." Clin Pharmacol Ther 83 (2008): 601-6[4] "Product Information. Camptosar (irinotecan)." Pharmacia and Upjohn, Kalamazoo, MI.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[7] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143063, 'Nelfinavir', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143226/', '[1] "Product Information. Chirocaine (levobupivacaine)" Organon, West Orange, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143064, 'Nelfinavir', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143227/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363024);
