INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143075, 'Niraparib', 'Nelfinavir', 'Moderate', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) may increase the bioavailability of niraparib, which is a substrate of the efflux transporter. Increased levels of niraparib may lead to increased incidence of hematological toxicity (e.g., thrombocytopenia, anemia, neutropenia) and/or cardiovascular toxicity (e.g., hypertension, hypertensive crisis).', NULL, 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', NULL, 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143238/', '[1] "Product Information. Zejula (niraparib)." Tesaro Inc., Waltham, MA.', 1767363024);
