INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143091, 'Paricalcitol', 'Nelfinavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of paricalcitol, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors. Serum calcium and phosphorus levels should be monitored closely. If clinically significant hypercalcemia develops, the paricalcitol dose should be reduced or interrupted and support therapy administered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143254/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143092, 'Pazopanib', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of pazopanib during and for 2 weeks after treatment with itraconazole. If coadministration is unavoidable, a reduction of the pazopanib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the pazopanib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Further dosage reductions may be needed if adverse effects occur during therapy. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the pazopanib dosage is adjusted upward.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143255/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363024);
