INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143099, 'Phenytoin', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may decrease the plasma concentrations of phenytoin. The proposed mechanism is nelfinavir induction of phenytoin metabolism via CYP450 2C9.', NULL, 'The potential for reduced therapeutic effects of phenytoin should be considered during coadministration with nelfinavir.', NULL, 'The potential for reduced therapeutic effects of phenytoin should be considered during coadministration with nelfinavir. Phenytoin serum levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of nelfinavir in patients who are stabilized on their anticonvulsant regimen. No dosage adjustment for nelfinavir is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143262/', '[1] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[2] Honda M, Yasuoka A, Aoki M, Oka S "A generalized seizure following initiation of nelfinavir in a patient with human immunodefieciency virus type 1 infection, suspected due to interaction between nelfinavir and phenytoin." Intern Med 38 (1999): 302-3[3] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000): 1333-9[4] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[5] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and anti