INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143119, 'Ramelteon', 'Nelfinavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations and pharmacologic effects of ramelteon, which is partially metabolized by these isoenzymes.', NULL, 'Caution is advised if ramelteon is prescribed in combination with potent inhibitors of CYP450 3A4 (e.', NULL, 'Caution is advised if ramelteon is prescribed in combination with potent inhibitors of CYP450 3A4 (e.g., itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, delavirdine, nefazodone, protease inhibitors, ketolide and certain macrolide antibiotics) and/or CYP450 2C9 (e.g., fluconazole, gemfibrozil, imatinib, miconazole). A reduction in the ramelteon dosage may be necessary in patients who experience excessive sedation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143282/', '[1] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.', 1767363024);
