INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143121, 'Ranolazine', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', NULL, 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ranolazine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143284/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[5] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143122, 'Regorafenib', 'Nelfinavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of regorafenib and decrease the plasma concentrations of its active metabolites.', NULL, 'Concomitant use of regorafenib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of regorafenib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of regorafenib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143285/', '[1] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143123, 'Relugolix', 'Nelfinavir', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally. Relugolix is a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours. Patients should be monitored more frequently for adverse effects. Alternatively, treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143286/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767363024);
