INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143232, 'Exenatide', 'Neomycin', 'Moderate', 'Drugs that affect renal function and/or hydration status may potentiate the adverse renal effects of exenatide. The mechanism of interaction is unknown. Exenatide has not been found to be directly nephrotoxic in preclinical or clinical studies.', NULL, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', NULL, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143395/', '[1] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.[2] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.', 1767363024);
