INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143487, 'Caspofungin', 'Nevirapine', 'Major', 'Coadministration with inducers or mixed inducer/inhibitors of certain metabolic pathways may produce clinically meaningful reductions in the plasma concentrations of caspofungin. This assumption is based on results from regression analyses of patient pharmacokinetic data derived from a small number of patients who were administered the drugs efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone, or carbamazepine prior to and/or concurrently with caspofungin. There are no data from formal drug interaction studies to evaluate these regression analyses, and it is not known which metabolic pathway involved in caspofungin clearance may be inducible.', NULL, 'The manufacturer of caspofungin states that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', NULL, 'The manufacturer of caspofungin states that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143650/', '[1] "Product Information. Cancidas (caspofungin)" Merck & Co, Inc, West Point, PA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143488, 'Cerivastatin', 'Nevirapine', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations and pharmacologic effects of certain HMG-CoA reductase inhibitors (i.e., statins) and/or their active metabolites. The proposed mechanism is efavirenz induction of CYP450 3A4 metabolism. Neither simvastatin, atorvastatin, nor pravastatin had any effect on the pharmacokinetics of efavirenz.', NULL, 'Coadministration of simvastatin, atorvastatin, or other statins that are primarily metabolized by CYP450 3A4 (e.', NULL, 'Coadministration of simvastatin, atorvastatin, or other statins that are primarily metabolized by CYP450 3A4 (e.g., lovastatin) with efavirenz may result in diminished hypolipidemic efficacy. Although pravastatin is not significantly metabolized by CYP450 3A4, it has also been implicated. Dosage adjustment of these statins may be necessary if they are prescribed with efavirenz. The same precaution may be applicable during therapy with nevirapine, another nonnucleoside reverse transcriptase inhibitor that induces CYP450 3A4.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143651/', '[1] Gerber JG, Rosenkranz SL, Fichtenbaum CJ, et al. "Effect of efavirenz on the pharmacokinetics of simvastatin, atorvastatin, and pravastatin: results of AIDS Clinical Trials Group 5108 Study." J Acquir Immune Defic Syndr 39 (2005): 307-12[2] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[3] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[4] Neuvonen PJ, Backman JT, Niemi M "Pharmacok