INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143497, 'Eplerenone', 'Nevirapine', 'Moderate', 'Coadministration with nevirapine may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to nevirapine-mediated induction of CYP450 3A4.', NULL, 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates.', NULL, 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates. Dosage adjustments as well as clinical and laboratory monitoring of coadministered drugs should be considered whenever nevirapine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143660/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Viramune XR (nevirapine)." Boehringer Ingelheim, Ridgefield, CT.[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143498, 'Erdafitinib', 'Nevirapine', 'Major', 'Coadministration with moderate inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', NULL, 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered.', NULL, 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered. If a moderate CYP450 2C9 or 3A4 inducer is coadministered after the initial dose increase period, the dose of erdafitinib may be increased up to 9 mg once daily based on serum phosphate levels and tolerability. When a moderate CYP450 2C9 or 3A4 inducer is discontinued, the current dose of erdafitinib may be continued in the absence of erdafitinib toxicity.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143661/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.[2] Cronk GA, Wheatley WB, Fellers GF, Albright H "The relationship of food intake to the absorption of potassium alpha-phenoxyethyl penicillin and potassium phenoxymethyl penicillin from the gastrointestinal tract." Am J Med Sci 240 (1960): 219-25[3] Neuvonen PJ, Elonen E, Pentikainen PJ "Comparative effect of food on absorption of ampicillin and pivampicillin." J Int Med Res 5 (1977):