INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143596, 'Ribociclib', 'Nevirapine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ribociclib, which is a substrate of the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine. Pharmacologic effects of ribociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143759/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143597, 'Rifampicin', 'Nevirapine', 'Major', 'Coadministration with rifampin may substantially decrease the plasma concentrations of nevirapine, although a few studies have suggested that the combination may be used effectively without dosage adjustments. The mechanism is rifampin induction of nevirapine metabolism via CYP450 3A4.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, nevirapine labeling and some experts recommend that alternative antimycobacterial agents be considered in patients already receiving effective nevirapine-containing antiretroviral therapy.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, nevirapine labeling and some experts recommend that alternative antimycobacterial agents be considered in patients already receiving effective nevirapine-containing antiretroviral therapy. For treatment of latent tuberculosis (TB) infection, a nine-month regimen of isoniazid may be considered if feasible. For treatment of HIV-related TB, a regimen that includes rifabutin is generally preferred, as rifabutin appears to be as effective as rifampin but is a much less potent inducer of CYP450 3A4. Nonrifamycin-containing regimens may be suboptimal (higher mortality rates; higher rates of treatment failure and relapse; increased adverse effects; longer treatment duration) and are usually not recommended for HIV-related TB except in patients who are intolerant of rifamycins or infected with a rifamycin-resistant isolate.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143760/', '[1] Veldkamp AI, Weverling GJ, Lange JM, et al. "High exposure to nevirapine in plasma is associated with an improved virological response in HIV-1-infected individuals." AIDS 15 (2001): 1089-95[2] Oliva J, Moreno S, Sanz J, et al. "Co-administration of rifampin and nevirapine in HIV-infected patients with tuberculosis." AIDS 17 (2003): 637-638[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] American Thoracic Society, CDC, Infectious Diseases Society of America "Treatment of tuberculosis." MMWR Morb Mortal Wkly Rep 52(RR-11) (2003): 1-77[6] Ribera E, Pou L, Lopez RM, et al "Pharmacokinetic interaction between nevirapine and rifampicin in HIV-infected patients with tuberculosis." J Acquir Immune Defic Syndr 28 (2001): 450-3[7] Burman WJ, Jones BE "Treatment of HIV-related tuberculosis in the era of effective antiretroviral therapy." Am J Respir Crit Care Med 164 (2001): 7-12[8] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[9] Dean GL, Back DJ, deRuiter A "Effect of tuberculosis therapy on nevirapine trough plasma concentrations." AIDS 13 (1999): 2489-90[10] Back D, Gibbons S, Khoo S "Pharmacokinetic drug interactions with nevirapine." J Acquir Immune Defic Syndr 34 Suppl 1 (2003): S8-14[11] "Notice to readers: updated guidelines for the use of rifabutin or rifampin for the treatment and prevention of tuberculosis among HIV-infected patients taking protease inhibitors or nonnucleoside reverse transcriptase inhibiotrs." MMWR Morb Mortal Wkly Rep 49 (2000): 185-9', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143598, 'Rifapentine', 'Nevirapine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143761/', '[1] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P4502C9 is the principal catalyst of racemic acenocoumarol hydroxylation reactions in human liver microsomes." Drug Metab Dispos 28 (2000): 1284-90[2] "Product Information. Priftin (rifapentine)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143599, 'Rimegepant', 'Nevirapine', 'Major', 'Coadministration with strong or moderate inducers of CYP450 3A4 may significantly reduce the plasma concentrations and therapeutic effects of rimegepant, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration of rimegepant with strong or moderate inducers of CYP450 3A4 should be avoided.', NULL, 'Coadministration of rimegepant with strong or moderate inducers of CYP450 3A4 should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143762/', '[1] "Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals, New Haven, CT.[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[3] "Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals, New Haven, CT.', 1767363024);
