INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143930, 'Fluvoxamine', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the plasma concentrations of nilotinib, which is a substrate of both the isoenzyme and the efflux transporter. Data are not available for nilotinib in combination with other CYP450 3A4 or P-glycoprotein inhibitors. Theoretically, a reverse interaction may also occur, since many CYP450 3A4 and P-glycoprotein inhibitors are also substrates, and nilotinib is an inhibitor of both.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors. Pharmacologic response to nilotinib should be monitored more closely whenever a CYP450 3A4 or P-glycoprotein inhibitor is added to or withdrawn from therapy, and the nilotinib dosage adjusted as necessary. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/144093/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143931, 'Fosamprenavir', 'Nilotinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme. Because nilotinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Concomitant use of nilotinib with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'Concomitant use of nilotinib with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of nilotinib during and for 2 weeks after treatment with itraconazole. Should treatment with a potent inhibitor be required, the manufacturer recommends that nilotinib therapy be withheld temporarily. If concomitant use is unavoidable, a reduction of the nilotinib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the nilotinib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/144094/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143932, 'Fosaprepitant', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the plasma concentrations of nilotinib, which is a substrate of both the isoenzyme and the efflux transporter. Data are not available for nilotinib in combination with other CYP450 3A4 or P-glycoprotein inhibitors. Theoretically, a reverse interaction may also occur, since many CYP450 3A4 and P-glycoprotein inhibitors are also substrates, and nilotinib is an inhibitor of both.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors. Pharmacologic response to nilotinib should be monitored more closely whenever a CYP450 3A4 or P-glycoprotein inhibitor is added to or withdrawn from therapy, and the nilotinib dosage adjusted as necessary. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/144095/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363024);
