INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143994, 'Nimodipine', 'Nilotinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with nilotinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter and/or CYP450 2D6, CYP450 3A4, or UGT1A1 isoenzymes. The mechanism is decreased clearance via these routes due to inhibition by nilotinib.', NULL, 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nilotinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/144157/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (143995, 'Nilotinib', 'Nintedanib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and CYP450 3A4 may increase the plasma concentrations of nintedanib, which is a substrate of the efflux transporter and a minor substrate of the isoenzyme.', NULL, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors.', NULL, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors. Pharmacologic response to nintedanib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the nintedanib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for increased adverse effects such as liver enzyme and bilirubin elevations, diarrhea, nausea, vomiting, gastrointestinal perforation, bleeding, and arterial thromboembolic events (e.g., myocardial infarction).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/144158/', '[1] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.', 1767363024);
