INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410210, 'Amphotericin B (cholesteryl sulfate)', 'Digoxin', 'Moderate', 'Although amphotericin does not alter the pharmacokinetics of digoxin, it may increase the risk of toxicity. The mechanism is reduction of serum potassium via renal potassium wasting.', 'DDInter', 1767317476, 'The patient''s potassium should be replenished, and serum potassium levels should be checked frequently during therapy. Electrocardiographic monitoring may also be indicated.', 'Others', 'The patient''s potassium should be replenished, and serum potassium levels should be checked frequently during therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
