INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410213, 'Digoxin', 'Atracurium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410214, 'Digoxin', 'Belladonna', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410215, 'Digoxin', 'Bendroflumethiazide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410216, 'Digoxin', 'Benzthiazide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410217, 'Digoxin', 'Bepridil', 'Moderate', 'Bepridil may increase serum digoxin levels. The mechanism is unknown. In addition, the negative chronotropic effects these drugs have on heart rate may be additive.', 'DDInter', 1767317476, 'Patients should be closely monitored during coadministration and serum digoxin levels should be obtained when digoxin toxicity is suspected. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual changes, slow pulse, or irregular heartbeats. Calcium channel blockers such as felodipine, or amlodipine, isradipine, and nicardipine appear to affect digoxin levels to a lesser extent or not at all and may be considered as alternatives.', 'Others', 'Patients should be closely monitored during coadministration and serum digoxin levels should be obtained when digoxin toxicity is suspected.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410218, 'Digoxin', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of the efflux transporter P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of P-gp activity by berotralstat.', 'DDInter', 1767317476, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of P-gp, particularly those with a narrow therapeutic index. Clinical and laboratory monitoring should be considered following the initiation or discontinuation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Excretion', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of P-gp, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410219, 'Digoxin', 'Bismuth subsalicylate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410220, 'Digoxin', 'Boceprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is reduced clearance of digoxin due to inhibition of P-glycoprotein-mediated renal tubular secretion.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used in combination with boceprevir or telaprevir. Pharmacologic response and serum digoxin levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with boceprevir or telaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410221, 'Digoxin', 'Bretylium', 'Moderate', 'Administration of bretylium may exacerbate digoxin toxicity and digoxin-induced arrhythmias. The mechanism is related to initial norepinephrine release induced by bretylium.', 'DDInter', 1767317476, 'Bretylium should be used cautiously in patients on digoxin and should be avoided if digitalis toxicity is present. Simultaneous initiation of therapy should also be avoided.', 'Synergism', 'Bretylium should be used cautiously in patients on digoxin and should be avoided if digitalis toxicity is present.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410222, 'Digoxin', 'Bromfenac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410223, 'Digoxin', 'Calcium carbonate', 'Moderate', 'Excessive administration of calcium can aggravate digitalis toxicity and precipitate serious cardiac arrhythmias in digitalised patients. The mechanism probably involves additive or synergistic inotropic effects of calcium and digitalis glycosides on the myocardium.', 'DDInter', 1767317476, 'Caution is advised if calcium preparations are used in patients receiving digitalis glycosides. Patients should be advised not to exceed the calcium dosage prescribed for them and to consult their healthcare provider before consuming additional products that may contain substantial amounts of calcium (e.g., over-the-counter supplements).', 'Synergism', 'Caution is advised if calcium preparations are used in patients receiving digitalis glycosides.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410224, 'Digoxin', 'Calcium chloride', 'Major', 'The administration of parenteral calcium preparations, particularly by rapid IV injection, may precipitate serious cardiac arrhythmias in digitalized patients. The mechanism is probably related to the additive or synergistic inotropic effects of calcium and digitalis glycosides on the myocardium.', 'DDInter', 1767317476, 'Intravenous calcium should preferably not be used in patients receiving digitalis glycosides, or it should be given slowly or in small amounts to avoid high serum calcium concentrations. Patients should be monitored for development of arrhythmias.', 'Synergism', 'Intravenous calcium should preferably not be used in patients receiving digitalis glycosides, or it should be given slowly or in small amounts to avoid high serum calcium concentrations.', NULL, NULL, 'Major', 'pharmacodynamic');
