INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410247, 'Doxorubicin (liposomal)', 'Digoxin', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', 'DDInter', 1767317476, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410248, 'Dronedarone', 'Digoxin', 'Major', 'Coadministration with dronedarone may significantly increase the serum concentrations of digoxin. The proposed mechanism is dronedarone inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'The need for digoxin therapy should be reconsidered prior to initiating dronedarone. If coadministration is deemed necessary, the digoxin dose should be reduced by one-half, and serum digoxin levels and pharmacologic effects closely monitored. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'The need for digoxin therapy should be reconsidered prior to initiating dronedarone.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410249, 'Elagolix', 'Digoxin', 'Moderate', 'Coadministration with elagolix may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by elagolix.', 'DDInter', 1767317476, 'Caution is advised when elagolix is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elagolix is added to or withdrawn from therapy.', 'Others', 'Caution is advised when elagolix is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410250, 'Eliglustat', 'Digoxin', 'Moderate', 'Coadministration with eliglustat may increase the serum concentrations of digoxin. The proposed mechanism is eliglustat inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Serum digoxin levels should be measured prior to initiating eliglustat therapy. Eliglustat product labeling recommends reducing the digoxin dose by 30% with continued monitoring of serum levels. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels should be measured prior to initiating eliglustat therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410251, 'Enalapril', 'Digoxin', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410252, 'Enasidenib', 'Digoxin', 'Moderate', 'Coadministration of enasidenib with digoxin may increase the plasma concentrations of digoxin. The proposed mechanism is decreased clearance of digoxin in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by enasidenib.', 'DDInter', 1767317476, 'Caution is advised when enasidenib is used concurrently with digoxin. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for digoxin whenever enasidenib is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Caution is advised when enasidenib is used concurrently with digoxin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410253, 'Encorafenib', 'Digoxin', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', 'DDInter', 1767317476, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410254, 'Enoxacin', 'Digoxin', 'Moderate', 'Some fluoroquinolones may significantly increase plasma digoxin levels. The mechanism has not been reported.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring for altered digoxin efficacy and safety is recommended during coadministration. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Dose adjustments may be required if an interaction is suspected.', 'Others', 'Clinical and laboratory monitoring for altered digoxin efficacy and safety is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410255, 'Enzalutamide', 'Digoxin', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410256, 'Ephedrine', 'Digoxin', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 1767317476, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410257, 'Epoprostenol', 'Digoxin', 'Minor', 'Epoprostenol may decrease clearance of oral digoxin. The mechanism has not been determined. Theoretically, this interaction could lead to higher digoxin plasma concentrations and could potentiate toxicity. Epoprostenol and digoxin have been used safely together during epoprostenol clinical trials.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410258, 'Erdafitinib', 'Digoxin', 'Moderate', 'Coadministration with erdafitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter, resulting in increased toxicity of these drugs. In vitro, erdafitinib has been shown to be an inhibitor of P-gp.', 'DDInter', 1767317476, 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided. If concomitant use is required, administer erdafitinib at least 6 hours before or after administration of P-gp substrates with narrow therapeutic ranges.', 'Distribution', 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410259, 'Ergocalciferol', 'Digoxin', 'Moderate', 'Excessive dosing of vitamin D can induce hypercalcemia, which may increase the risk of digitalis toxicity and serious arrhythmias due to additive inotropic effects.', 'DDInter', 1767317476, 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs. Serum digoxin, calcium, and phosphorus levels should be closely monitored, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia.', 'Synergism', 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410260, 'Erythromycin', 'Digoxin', 'Moderate', 'Macrolide antibiotics that inhibit P-glycoprotein may increase the plasma concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin. The interaction has been described in numerous case reports in the medical literature, the majority of which involved clarithromycin, a potent P-glycoprotein inhibitor. Some patients have shown clinical signs consistent with digoxin toxicity, including potentially fatal arrhythmias. Exposure to macrolides, specifically clarithromycin, azithromycin and erythromycin, has been identified as a risk factor for digoxin toxicity.', 'DDInter', 1767317476, 'Caution is advised when digoxin or digitoxin is used in combination with macrolide antibiotics that are inhibitors of P-glycoprotein. Serum digitalis levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the macrolide in patients who are stabilized on their digitalis regimen. Patients should be advised to notify their physician if they experience signs of digitalis toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Absorption, Excretion', 'Caution is advised when digoxin or digitoxin is used in combination with macrolide antibiotics that are inhibitors of P-glycoprotein.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410261, 'Esmolol', 'Digoxin', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410262, 'Esomeprazole', 'Digoxin', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', 'DDInter', 1767317476, 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', 'Caution is advised if digoxin is prescribed in combination with PPIs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410263, 'Estazolam', 'Digoxin', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410264, 'Etacrynic acid', 'Digoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410265, 'Ethotoin', 'Digoxin', 'Moderate', 'Limited clinical data suggest that hydantoins may reduce digoxin serum levels. The mechanism of this interaction is unknown. Although phenytoin has been used in the treatment of digitalis-induced arrhythmias, it has been associated with cardiac arrest in patients with marked heart block.', 'DDInter', 1767317476, 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended. Occasionally, increases in the digoxin dosage may be necessary. The clinician should be aware that the risk of digoxin toxicity may be increased when a hydantoin is discontinued and should consider monitoring the patient more closely for those effects. Phenytoin should be avoided in patients with marked digoxin-induced heart block or bradycardia.', 'Others', 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410266, 'Etodolac', 'Digoxin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410267, 'Etravirine', 'Digoxin', 'Moderate', 'Coadministration of digoxin with etravirine may increase serum digoxin concentrations. The mechanism is unknown, but etravirine inhibition of p-glycoprotein-mediated transport may be involved.', 'DDInter', 1767317476, 'Caution and monitoring of serum digoxin levels are advised during concomitant use. The lowest dose of digoxin is recommended at initiation of digoxin therapy in patients receiving etravirine. Dose adjustments are not necessary upon initiation of etravirine therapy in patients who are stabilized on digoxin. Digoxin levels should be monitored and the dose titrated accordingly, as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Absorption', 'Caution and monitoring of serum digoxin levels are advised during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410268, 'Exenatide', 'Digoxin', 'Minor', 'Coadministration with exenatide may modestly affect the pharmacokinetic disposition of digoxin. The mechanism of interaction has not been described.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410269, 'Ezogabine', 'Digoxin', 'Moderate', 'Data from an in vitro study showed that the N-acetyl metabolite of ezogabine (NAMR) inhibited P-glycoprotein-mediated transport of digoxin in a concentration-dependent manner, indicating that NAMR may inhibit renal clearance of digoxin. It is possible that administration of ezogabine at therapeutic dosages may increase digoxin serum concentrations.', 'DDInter', 1767317476, 'Pharmacologic response and serum digoxin levels should be monitored more closely following the addition, discontinuation, or change of dosage of ezogabine, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Pharmacologic response and serum digoxin levels should be monitored more closely following the addition, discontinuation, or change of dosage of ezogabine, and the digoxin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410270, 'Fenoprofen', 'Digoxin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410271, 'Fingolimod', 'Digoxin', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317476, 'Fingolimod has not been adequately studied in patients receiving beta-blockers, calcium channel blockers, or digitalis. The possibility of switching to alternative agents that do not slow heart rate or AV conduction should be evaluated by the physician before initiating fingolimod. In patients who cannot switch, overnight continuous ECG monitoring after the first dose is recommended in accordance with the product labeling. The same precautions are applicable if, after the first month of treatment, fingolimod is discontinued for more than two weeks and then restarted, since the effects on heart rate and AV conduction may recur on reintroduction of fingolimod. Within the first 2 weeks of treatment, first-dose procedures are also recommended after interruption of one day or more; during week 3 and 4 of treatment, first-dose procedures are recommended after treatment interruption of more than 7 days. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', 'Fingolimod has not been adequately studied in patients receiving beta-blockers, calcium channel blockers, or digitalis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410272, 'Flavoxate', 'Digoxin', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410273, 'Flecainide', 'Digoxin', 'Minor', 'Flecainide may cause a slight increase in plasma digoxin levels. The mechanism is unknown. In one case, an increase in the PR interval was associated with this interaction, but no clinically significant adverse effects have been reported. If digoxin levels are in the upper end of the therapeutic range before flecainide is administered, caution is recommended. If digoxin toxicity develops or is suspected during coadministration, a decrease in digoxin dosage should be considered. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410274, 'Flibanserin', 'Digoxin', 'Major', 'Coadministration with flibanserin may significantly increase the serum concentrations of digoxin. The proposed mechanism is flibanserin inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of flibanserin, and the digoxin dosage adjusted as needed. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of flibanserin, and the digoxin dosage adjusted as needed.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410275, 'Fludarabine', 'Digoxin', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', 'DDInter', 1767317476, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410276, 'Fludrocortisone', 'Digoxin', 'Moderate', 'Systemically administered corticosteroids may induce hypokalemia and possibly increase the risk of digoxin toxicity. In addition, corticosteroid-induced sodium and water retention can result in edema leading to heart failure. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) which have greater mineralocorticoid activity.', 'DDInter', 1767317476, 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', 'Others', 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410277, 'Fluoxetine', 'Digoxin', 'Minor', 'case report has suggested that fluoxetine may increase serum digoxin concentrations. The mechanism and clinical significance is unknown. Causality was not clearly established due to the presence of other medications. Dose adjustments may be required if an interaction is suspected.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410278, 'Flurazepam', 'Digoxin', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410279, 'Flurbiprofen', 'Digoxin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410280, 'Fluvastatin', 'Digoxin', 'Minor', 'Limited data suggest that fluvastatin may slightly increase the plasma clearance of digoxin by an unknown mechanism. The clinical significance of this interaction is unknown. Digoxin serum concentrations and clinical response should be monitored during concomitant therapy. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410281, 'Gatifloxacin', 'Digoxin', 'Moderate', 'Some fluoroquinolones may significantly increase plasma digoxin levels. The mechanism has not been reported.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring for altered digoxin efficacy and safety is recommended during coadministration. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Dose adjustments may be required if an interaction is suspected.', 'Others', 'Clinical and laboratory monitoring for altered digoxin efficacy and safety is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410282, 'Gentamicin', 'Digoxin', 'Minor', 'Animal data show that serum digoxin levels increase after administration of nontoxic doses of gentamicin. The digoxin doses were five times that usually recommended for healthy human adults with normal renal function (on a per kg basis). Signs of digoxin toxicity were not apparent until toxic doses of gentamicin were given. These data may not apply to humans, and the clinical relevance is not clear. At this time, no significant clinical interaction is suspected in humans.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410283, 'Ginseng', 'Digoxin', 'Moderate', 'Asian and Siberian ginseng may interfere with digoxin either pharmacodynamically or with its monitoring via certain immunoassays. The interaction has been observed in vitro as well as in nondigoxin-treated mice that were fed ginseng and is thought to be due to substances in the ginseng that structurally resemble cardiac glycosides. Serum pools prepared from samples from patients receiving digoxin and then supplemented with Asian or Siberian ginseng also demonstrated interference.', 'DDInter', 1767317476, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Use of ginseng should probably be avoided in patients treated with cardiac glycosides.', 'Others', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410284, 'Glasdegib', 'Digoxin', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.', 'DDInter', 1767317476, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410285, 'Glycerin', 'Digoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410286, 'Halazepam', 'Digoxin', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410287, 'Heparin', 'Digoxin', 'Minor', 'Limited data reveal that the free fraction of digoxin is increased in uremic patients following administration of heparin after five minutes of hemodialysis. The mechanism is displacement of digoxin from albumin-binding sites. The resulting increase in digoxin was not associated with toxicity and was clinically insignificant. However, this interaction may be important as stable doses digoxin are established in some patients receiving heparin infusions. More data are needed.', 'DDInter', 1767317476, '-', 'Distribution', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410288, 'Hydroflumethiazide', 'Digoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410289, 'Hydroxychloroquine', 'Digoxin', 'Moderate', 'Coadministration with hydroxychloroquine may increase the serum concentrations of digoxin. The mechanism of interaction has not been established.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used with hydroxychloroquine. Pharmacologic response and serum digoxin levels should be monitored more closely whenever hydroxychloroquine is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience signs and symptoms of digoxin toxicity such as irregular heartbeats, slow pulse, nausea, anorexia, or visual changes.', 'Others', 'Caution is advised if digoxin must be used with hydroxychloroquine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410290, 'Ibrutinib', 'Digoxin', 'Moderate', 'Coadministration with ibrutinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter. In vitro studies indicate that ibrutinib is not a substrate for P-gp, and systemic ibrutinib is unlikely to be an inhibitor of P-gp at clinical doses.', 'DDInter', 1767317476, 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range. Dosage reduction and/or dosage interval adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ibrutinib is added to or withdrawn from therapy. Some authorities recommend that narrow therapeutic range P-gp substrates, such as digoxin, should be administered at least 6 hours before or after ibrutinib.', 'Absorption', 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410291, 'Ibutilide', 'Digoxin', 'Moderate', 'Coadministration of digoxin does not appear to significantly affect the kinetics, safety, or efficacy of ibutilide. The clinician should be aware, however, that supraventricular arrhythmias may mask the cardiotoxicity associated with excessive digoxin levels.', 'DDInter', 1767317476, 'It is advisable to be particularly cautious with patients in whom digoxin toxicity is suspected and with patients whose plasma digoxin levels are above normal or are suspected to be above the usual therapeutic range.', 'Synergism', 'It is advisable to be particularly cautious with patients in whom digoxin toxicity is suspected and with patients whose plasma digoxin levels are above normal or are suspected to be above the usual therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410292, 'Indapamide', 'Digoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410293, 'Indomethacin', 'Digoxin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410294, 'Isavuconazonium', 'Digoxin', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the bioavailability of digoxin. The proposed mechanism is isavuconazole inhibition of the P-glycoprotein-mediated intestinal or renal efflux of digoxin.', 'DDInter', 1767317476, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of isavuconazonium sulfate. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of isavuconazonium sulfate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410295, 'Isoprenaline', 'Digoxin', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 1767317476, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410296, 'Istradefylline', 'Digoxin', 'Moderate', 'Coadministration of istradefylline may increase the serum concentrations of digoxin. The proposed mechanism is istradefylline inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of istradefylline, and the digoxin dosage adjusted as needed. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of istradefylline, and the digoxin dosage adjusted as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410297, 'Itraconazole', 'Digoxin', 'Major', 'Coadministration with itraconazole may significantly increase the serum concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein-mediated intestinal efflux or renal tubular secretion of digoxin by itraconazole, a potent inhibitor of the transporter.', 'DDInter', 1767317476, 'Caution is advised when digoxin is administered with itraconazole. The prescribing information for digoxin recommends decreasing the dosage by approximately 30% to 50% or modifying the dosing frequency when initiating itraconazole, followed by continued therapeutic monitoring of digoxin. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption, Excretion', 'Caution is advised when digoxin is administered with itraconazole.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410298, 'Ivabradine', 'Digoxin', 'Moderate', 'Coadministration of ivabradine with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, and digitalis may increase the risk of excessive bradycardia or other conduction disturbances. Non-cardiac drugs that may also cause bradycardia include atazanavir, fingolimod, lacosamide, lithium, mefloquine, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', 'DDInter', 1767317476, 'Caution is advised when ivabradine is prescribed with other bradycardic drugs. Heart rate should be closely monitored, and the dosage of ivabradine adjusted accordingly. Patients should be advised to report significant decreases in heart rate or symptoms such as dizziness, lightheadedness, fatigue, and hypotension.', 'Synergism', 'Caution is advised when ivabradine is prescribed with other bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410299, 'Ivacaftor', 'Digoxin', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 1767317476, 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410300, 'Digoxin', 'Kanamycin', 'Minor', 'Oral neomycin has been shown to decrease the absorption of oral digoxin. The effects of oral paromomycin and kanamycin are not known. The proposed mechanism is a neomycin-induced malabsorption syndrome resulting in reduced absorption of drugs. This effect may be offset by a subsequent decrease in digoxin metabolism. During concomitant therapy with any of these antibiotics, digoxin serum concentrations and efficacy should be monitored. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410301, 'Digoxin', 'Kaolin', 'Moderate', 'INTERVAL: Concurrent administration of kaolin-pectin may decrease the oral bioavailability of digitalis glycosides. The mechanism of interaction is unknown.', 'DDInter', 1767317476, 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible. The same precaution may be applicable during therapy with digitoxin, although clinical data are lacking.', 'Absorption', 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410302, 'Digoxin', 'Ketoconazole', 'Moderate', 'Coadministration with ketoconazole may increase the serum concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein-mediated intestinal efflux or renal tubular secretion of digoxin by ketoconazole. Available data are conflicting.', 'DDInter', 1767317476, 'Caution is advised when digoxin is administered with ketoconazole. Serum digoxin levels should be checked regularly and the dosage adjusted accordingly, particularly following initiation or discontinuation of ketoconazole in patients who are stabilized on their digoxin regimen. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Caution is advised when digoxin is administered with ketoconazole.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410303, 'Digoxin', 'Ketoprofen', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410304, 'Digoxin', 'Labetalol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410305, 'Digoxin', 'Lacosamide', 'Moderate', 'Lacosamide has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, digoxin, atazanavir, mefloquine) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317476, 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block, second-degree or higher AV block and sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. An ECG should be obtained in these patients before initiating lacosamide and after titration to steady-state.', 'Synergism', 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410306, 'Digoxin', 'Lanreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 1767317476, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410307, 'Digoxin', 'Lansoprazole', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', 'DDInter', 1767317476, 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', 'Caution is advised if digoxin is prescribed in combination with PPIs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410308, 'Digoxin', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: Theoretically, lanthanum carbonate may chelate with certain drugs in the gastrointestinal tract, resulting in reduced oral bioavailability of those drugs during coadministration.', 'DDInter', 1767317476, 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.g., ACE inhibitors, beta blockers, bisphosphonates, coumarin derivatives, digitalis glycosides, fluoroquinolones, iron, phenytoin, rifampin, tetracyclines, thyroid preparations, valproic acid) should not be taken within 2 hours of administration of lanthanum carbonate according to the product labeling.', 'Absorption', 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410309, 'Digoxin', 'Lapatinib', 'Major', 'Coadministration with lapatinib may significantly increase the plasma concentrations of digoxin. The proposed mechanism is lapatinib inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Caution is advised if digoxin is used in combination with lapatinib. Serum digoxin levels and pharmacologic effects should be closely monitored during coadministration and following the discontinuation of lapatinib, and the digoxin dosage adjusted as necessary. If digoxin serum concentration is above 1.2 ng/mL, the digoxin dosage should be reduced by one-half. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Caution is advised if digoxin is used in combination with lapatinib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410310, 'Digoxin', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', 'DDInter', 1767317476, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410311, 'Digoxin', 'Lenalidomide', 'Moderate', 'Coadministration with lenalidomide may increase the plasma concentrations of digoxin. The mechanism of interaction is unknown.', 'DDInter', 1767317476, 'It may be appropriate to monitor pharmacologic response and serum digoxin levels more closely whenever lenalidomide is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience signs and symptoms of digoxin toxicity such as irregular heartbeats, slow pulse, nausea, anorexia, or visual changes.', 'Others', 'It may be appropriate to monitor pharmacologic response and serum digoxin levels more closely whenever lenalidomide is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410312, 'Digoxin', 'Levosalbutamol', 'Moderate', 'Coadministration with albuterol decreases digoxin''s serum concentration. The mechanism of action may involve an albuterol-induced increased distribution of digoxin to skeletal muscle.', 'DDInter', 1767317476, 'Caution is advised if albuterol is used concomitantly with digoxin. Monitoring of serum digoxin and potassium concentrations may be required. Clinical monitoring for digoxin toxicity and/or hypokalemia, particularly in patients at risk of hypokalemia should also be considered. Dosage adjustments for either medication may also be required whenever digoxin and albuterol are used concurrently. Patients should be advised to seek immediate medical attention if they experience signs of arrhythmias, such as sudden dizziness, lightheadedness, fainting, shortness of breath, or irregular heartbeats.', 'Distribution', 'Caution is advised if albuterol is used concomitantly with digoxin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410313, 'Digoxin', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410314, 'Digoxin', 'Levobunolol (ophthalmic)', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410315, 'Digoxin', 'Levothyroxine', 'Moderate', 'The clearance of or sensitivity to digitalis glycosides may be increased in previously hypothyroid patients when a euthyroid state is achieved after the addition of thyroid hormones.', 'DDInter', 1767317476, 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when thyroid hormones are added, discontinued, or dosing changes are made. Digitalis dose increases may be required.', 'Metabolism', 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when thyroid hormones are added, discontinued, or dosing changes are made.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410316, 'Digoxin', 'Licorice', 'Moderate', 'Chronic use of licorice may potentiate the risk of digitalis toxicity and other cardiovascular complications in patients treated with cardiac glycosides. Glycyrrhizic acid, a component of licorice, can induce hypokalemia, hypernatremia, edema, hypertension, and suppression of the renin-aldosterone system by virtue of its mineralocorticoid activity. In digitalized patients, electrolyte imbalance such as hypokalemia and hypomagnesemia can lead to arrhythmias, while other chronic effects of glycyrrhizic acid can compromise overall cardiovascular condition.', 'DDInter', 1767317476, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, chronic use of licorice and licorice-containing products should be avoided in patients treated with cardiac glycosides.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410317, 'Digoxin', 'Liothyronine', 'Moderate', 'The clearance of or sensitivity to digitalis glycosides may be increased in previously hypothyroid patients when a euthyroid state is achieved after the addition of thyroid hormones.', 'DDInter', 1767317476, 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when thyroid hormones are added, discontinued, or dosing changes are made. Digitalis dose increases may be required.', 'Metabolism', 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when thyroid hormones are added, discontinued, or dosing changes are made.', NULL, NULL, 'Moderate', 'pharmacodynamic');
