INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410319, 'Digoxin', 'Liraglutide', 'Minor', 'Liraglutide delays gastric emptying, which may impact the absorption of concomitantly administered oral medications.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410320, 'Digoxin', 'Lisinopril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410321, 'Digoxin', 'Lomitapide', 'Moderate', 'Coadministration with lomitapide may increase the plasma concentrations of drugs that are substrates of P-glycoprotein. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of P-glycoprotein-mediated drug efflux by lomitapide.', 'DDInter', 1767317476, 'Caution is advised if lomitapide must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lomitapide is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised if lomitapide must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410322, 'Digoxin', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410323, 'Digoxin', 'Lorazepam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410324, 'Digoxin', 'Lorlatinib', 'Moderate', 'Coadministration of lorlatinib with drugs that are P-glycoprotein (P-gp) substrates may decrease the plasma concentration and pharmacologic effect of the P-gp substrate. The proposed mechanism is induction of P-gp mediated drug efflux by lorlatinib.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures. If coadministration is required, consider dosage adjustments and clinical and laboratory monitoring whenever lorlatinib is added to or withdrawn from therapy.', 'Excretion', 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410325, 'Digoxin', 'Magaldrate', 'Minor', 'Concurrent administration of antacids may decrease the oral bioavailability of digoxin and digitoxin. When digoxin and digitoxin are coadministered with Suprep Bowel Prep (magnesium/potassium/sodium sulfates), the manufacturer recommends administering digoxin or digitoxin at least 2 hours before and not less than 6 hours after Suprep Bowel Prep to avoid chelation with magnesium.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410326, 'Digoxin', 'Magnesium carbonate', 'Minor', 'Concurrent administration of antacids may decrease the oral bioavailability of digoxin and digitoxin. When digoxin and digitoxin are coadministered with Suprep Bowel Prep (magnesium/potassium/sodium sulfates), the manufacturer recommends administering digoxin or digitoxin at least 2 hours before and not less than 6 hours after Suprep Bowel Prep to avoid chelation with magnesium.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
