INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410329, 'Digoxin', 'Mannitol', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410330, 'Digoxin', 'Meclofenamic acid', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410331, 'Digoxin', 'Mefenamic acid', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410332, 'Digoxin', 'Mepenzolate', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410333, 'Digoxin', 'Mephenytoin', 'Moderate', 'Limited clinical data suggest that hydantoins may reduce digoxin serum levels. The mechanism of this interaction is unknown. Although phenytoin has been used in the treatment of digitalis-induced arrhythmias, it has been associated with cardiac arrest in patients with marked heart block.', 'DDInter', 1767317476, 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended. Occasionally, increases in the digoxin dosage may be necessary. The clinician should be aware that the risk of digoxin toxicity may be increased when a hydantoin is discontinued and should consider monitoring the patient more closely for those effects. Phenytoin should be avoided in patients with marked digoxin-induced heart block or bradycardia.', 'Others', 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410334, 'Digoxin', 'Metaraminol', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 1767317476, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410335, 'Digoxin', 'Methazolamide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410336, 'Digoxin', 'Methimazole', 'Moderate', 'The clearance of digitalis glycosides may be reduced when a euthyroid state is achieved after the addition of antithyroid agents.', 'DDInter', 1767317476, 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when antithyroid agents are added, discontinued, or dosing changes are made. Digitalis dose reductions may be required.', 'Excretion', 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when antithyroid agents are added, discontinued, or dosing changes are made.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410337, 'Digoxin', 'Methotrexate', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', 'DDInter', 1767317476, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410338, 'Digoxin', 'Methscopolamine', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410339, 'Digoxin', 'Methylprednisolone', 'Moderate', 'Systemically administered corticosteroids may induce hypokalemia and possibly increase the risk of digoxin toxicity. In addition, corticosteroid-induced sodium and water retention can result in edema leading to heart failure. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) which have greater mineralocorticoid activity.', 'DDInter', 1767317476, 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', 'Others', 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410340, 'Digoxin', 'Metipranolol (ophthalmic)', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410341, 'Digoxin', 'Metoclopramide', 'Minor', 'Digoxin serum concentrations may be reduced during concomitant therapy with metoclopramide. The proposed mechanism is metoclopramide-induced stimulation of gastric motility, which may decrease digoxin absorption. Rapidly dissolving preparations such as digoxin solution in capsules do not appear to be affected. Patients should be advised to notify their physicians if they experience worsening of their heart symptoms.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410342, 'Digoxin', 'Metocurine', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410343, 'Digoxin', 'Metolazone', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410344, 'Digoxin', 'Metoprolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410345, 'Digoxin', 'Midazolam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410346, 'Digoxin', 'Midodrine', 'Moderate', 'Midodrine when administered with any agent that may slow the heart rate (i.e., cardiac glycosides, beta blockers, tricyclic antidepressants) may enhance or precipitate bradycardia, AV block or arrhythmia. The mechanism may be related to vagal reflexive slowing of the heart after midodrine administration.', 'DDInter', 1767317476, 'If midodrine and cardiac glycosides are administered concomitantly, patients should be monitored for cardiac abnormalities. Patients should be advised to promptly notify their physician if they experience a slow pulse, dizziness, syncope, or cardiac awareness.', 'Synergism', 'If midodrine and cardiac glycosides are administered concomitantly, patients should be monitored for cardiac abnormalities.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410347, 'Digoxin', 'Midostaurin', 'Moderate', 'Coadministration with midostaurin may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic anion transporting polypeptide (OATP) 1B1 transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to midostaurin-mediated inhibition of P-gp, BCRP, and/or OATP1B1 transport proteins.', 'DDInter', 1767317476, 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever midostaurin is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if midostaurin is used concomitantly with drugs that are substrates of P-gp, BCRP, and/or OATP1B1 transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410348, 'Digoxin', 'Mifepristone', 'Moderate', 'Coadministration with mifepristone may increase the plasma concentrations of digoxin. The proposed mechanism is mifepristone inhibition of P-glycoprotein-mediated intestinal efflux of digoxin. When digoxin 0.125 mg once daily was administered with mifepristone 1200 mg once daily for 10 days in study subjects, mean digoxin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 1.6- and 1.4-fold, respectively, compared to administration of digoxin alone.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used concomitantly with mifepristone. Dosage adjustments as well as clinical and laboratory monitoring are appropriate for digoxin whenever mifepristone is added to or withdrawn from therapy. The manufacturer recommends that plasma digoxin concentration be measured after 1 to 2 weeks of concomitant use and periodically as necessary thereafter. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Absorption', 'Caution is advised if digoxin must be used concomitantly with mifepristone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410349, 'Digoxin', 'Milnacipran', 'Moderate', 'Use of milnacipran concomitantly with digoxin may be associated with potentiation of adverse hemodynamic effects. Inhibition of norepinephrine and serotonin reuptake by milnacipran can lead to cardiovascular effects including sustained increases in blood pressure and heart rate. However, effects of milnacipran in patients with significant hypertension, arrhythmia, or cardiac disease have not been systematically evaluated.', 'DDInter', 1767317476, 'Coadministration of milnacipran and intravenous digoxin should be avoided. Caution is advised during coadministration with oral digoxin therapy. Blood pressure and heart rate should be measured prior to initiating milnacipran and periodically throughout treatment. A dosage reduction or discontinuation should be considered in patients who experience a sustained increase in blood pressure or heart rate while receiving milnacipran.', 'Synergism', 'Coadministration of milnacipran and intravenous digoxin should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410350, 'Digoxin', 'Minocycline', 'Moderate', 'Tetracyclines may increase serum levels of orally administered digoxin in about 10% of the population. The mechanism may be related to changes in intestinal flora that alter the absorption of digoxin. The risk of an interaction may be less with digoxin solution in capsules because absorption occurs in the upper GI tract.', 'DDInter', 1767317476, 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended. Digoxin dosage reductions may be required. Patients should be advised to notify their physicians if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Absorption', 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410351, 'Digoxin', 'Mirabegron', 'Moderate', 'Coadministration with mirabegron may increase the serum concentrations of digoxin. The exact mechanism of interaction has not been established. However, since both agents are substrates for the P-glycoprotein efflux transporter, competitive inhibition is possible.', 'DDInter', 1767317476, 'The lowest dosage of digoxin should initially be considered when prescribed in combination with mirabegron. Pharmacologic response and serum digoxin levels should be monitored, and digoxin dosage adjusted as needed to attain desired clinical effect.', 'Others', 'The lowest dosage of digoxin should initially be considered when prescribed in combination with mirabegron.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410352, 'Digoxin', 'Mivacurium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410353, 'Digoxin', 'Moexipril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410354, 'Digoxin', 'Moricizine', 'Moderate', 'Moricizine does not significantly affect the pharmacokinetics of digoxin in patients with normal renal function. However, because of possible additive effects on AV conduction, prolongation of the PR interval and AV conduction disturbances are predicted.', 'DDInter', 1767317476, 'The electrocardiogram should be closely monitored during coadministration.', 'Synergism', 'The electrocardiogram should be closely monitored during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410355, 'Digoxin', 'Nabumetone', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410356, 'Digoxin', 'Nadolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410357, 'Digoxin', 'Naproxen', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410358, 'Digoxin', 'Nebivolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410359, 'Digoxin', 'Nefazodone', 'Moderate', 'Nefazodone can increase serum digoxin concentration by almost 30%. The mechanism is unknown.', 'DDInter', 1767317476, 'Patients receiving digoxin should be monitored closely when nefazodone is added to or deleted from the regimen, and the digoxin dosage adjusted accordingly. Patients should be advised to notify their physicians if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Patients receiving digoxin should be monitored closely when nefazodone is added to or deleted from the regimen, and the digoxin dosage adjusted accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410360, 'Digoxin', 'Neomycin', 'Minor', 'Oral neomycin has been shown to decrease the absorption of oral digoxin. The effects of oral paromomycin and kanamycin are not known. The proposed mechanism is a neomycin-induced malabsorption syndrome resulting in reduced absorption of drugs. This effect may be offset by a subsequent decrease in digoxin metabolism. During concomitant therapy with any of these antibiotics, digoxin serum concentrations and efficacy should be monitored. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410361, 'Digoxin', 'Neratinib', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by neratinib.', 'DDInter', 1767317476, 'Caution is advised when neratinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever neratinib is added to or withdrawn from therapy.', 'Others', 'Caution is advised when neratinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410362, 'Digoxin', 'Nilotinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with nilotinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter and/or CYP450 2D6, CYP450 3A4, or UGT1A1 isoenzymes. The mechanism is decreased clearance via these routes due to inhibition by nilotinib.', 'DDInter', 1767317476, 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nilotinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410363, 'Digoxin', 'Nisoldipine', 'Moderate', 'Limited data suggest that nifedipine may decrease digoxin clearance. Serum digoxin levels and risk of toxicity may be increased. The effect may be dose-dependent, however data are conflicting. Other calcium channel blockers may also interact. Higher doses of nisoldipine may cause small increases in digoxin levels.', 'DDInter', 1767317476, 'Serum digoxin levels should be checked when clinically necessary. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual changes, slow pulse, or irregular heartbeats. If digoxin toxicity is suspected, digoxin dosage should be withheld or reduced.', 'Metabolism', 'Serum digoxin levels should be checked when clinically necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410364, 'Digoxin', 'Norepinephrine', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 1767317476, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410365, 'Digoxin', 'Octreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 1767317476, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410366, 'Digoxin', 'Olaparib', 'Moderate', 'Based on in vitro inhibition data, coadministration with olaparib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 (e.g., cisapride, ergot alkaloids, fentanyl, lovastatin, oral midazolam, pimozide, quetiapine, simvastatin, triazolam, vinca alkaloids), P-gp (e.g., colchicine, dabigatran, digoxin), breast cancer resistance protein (BCRP) (e.g., rosuvastatin), OATP1B1 (e.g., eluxadoline, glyburide, repaglinide, statins, valsartan), or organic cation transporter 1 or 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism is decreased clearance due to inhibition of the corresponding metabolizing enzyme and/or efflux/uptake transporter by olaparib.', 'DDInter', 1767317476, 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever olaparib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410367, 'Digoxin', 'Omeprazole', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', 'DDInter', 1767317476, 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', 'Caution is advised if digoxin is prescribed in combination with PPIs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410368, 'Digoxin', 'Oxazepam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410369, 'Digoxin', 'Oxybutynin', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410370, 'Digoxin', 'Oxytetracycline', 'Moderate', 'Tetracyclines may increase serum levels of orally administered digoxin in about 10% of the population. The mechanism may be related to changes in intestinal flora that alter the absorption of digoxin. The risk of an interaction may be less with digoxin solution in capsules because absorption occurs in the upper GI tract.', 'DDInter', 1767317476, 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended. Digoxin dosage reductions may be required. Patients should be advised to notify their physicians if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Absorption', 'Clinical monitoring of patient response and tolerance, including laboratory serum digoxin levels is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410371, 'Digoxin', 'Ozanimod', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', 'DDInter', 1767317476, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410372, 'Digoxin', 'Pancuronium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410373, 'Digoxin', 'Pantoprazole', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', 'DDInter', 1767317476, 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', 'Caution is advised if digoxin is prescribed in combination with PPIs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410374, 'Digoxin', 'Parathyroid hormone', 'Major', 'Parathyroid hormone may alter the effects of digitalis glycosides. The inotropic effects of digitalis glycosides are affected by serum calcium levels. Since parathyroid hormone causes a transient increase in serum calcium, it can potentiate the risk of digitalis toxicity if hypercalcemia develops. On the other hand, digitalis efficacy may be reduced if hypocalcemia occurs. Severe cases of both hypercalcemia and hypocalcemia have been reported during treatment with parathyroid hormone. The risk of hypercalcemia is greatest when starting or increasing the dose of parathyroid hormone, whereas the risk of hypocalcemia is greatest when parathyroid hormone is withheld, missed, or abruptly discontinued.', 'DDInter', 1767317476, 'Serum calcium and digitalis levels should be closely monitored during parathyroid hormone therapy, and the digitalis dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Others', 'Serum calcium and digitalis levels should be closely monitored during parathyroid hormone therapy, and the digitalis dosage adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410375, 'Digoxin', 'Paricalcitol', 'Moderate', 'Excessive dosing of vitamin D can induce hypercalcemia, which may increase the risk of digitalis toxicity and serious arrhythmias due to additive inotropic effects.', 'DDInter', 1767317476, 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs. Serum digoxin, calcium, and phosphorus levels should be closely monitored, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia.', 'Synergism', 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410376, 'Digoxin', 'Paromomycin', 'Minor', 'Oral neomycin has been shown to decrease the absorption of oral digoxin. The effects of oral paromomycin and kanamycin are not known. The proposed mechanism is a neomycin-induced malabsorption syndrome resulting in reduced absorption of drugs. This effect may be offset by a subsequent decrease in digoxin metabolism. During concomitant therapy with any of these antibiotics, digoxin serum concentrations and efficacy should be monitored. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410377, 'Digoxin', 'Paroxetine', 'Minor', 'The manufacturer of paroxetine reports that paroxetine may decrease serum digoxin levels by 15%; however, no changes in concentration have also been reported. The mechanism and clinical significance is unknown. If paroxetine and digoxin are used together, monitoring for clinical and laboratory evidence of altered digoxin effect is recommended. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410378, 'Digoxin', 'Pasireotide', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', 'DDInter', 1767317476, 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410379, 'Digoxin', 'Pectin', 'Moderate', 'INTERVAL: Concurrent administration of kaolin-pectin may decrease the oral bioavailability of digitalis glycosides. The mechanism of interaction is unknown.', 'DDInter', 1767317476, 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible. The same precaution may be applicable during therapy with digitoxin, although clinical data are lacking.', 'Absorption', 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410380, 'Digoxin', 'Penbutolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410381, 'Digoxin', 'Penicillamine', 'Moderate', 'Coadministration with penicillamine may decrease the serum concentrations of digoxin.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used with penicillamine. Pharmacologic response and serum digoxin levels should be monitored more closely whenever penicillamine is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience worsening of their heart symptoms during coadministration with penicillamine.', 'Excretion', 'Caution is advised if digoxin must be used with penicillamine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410382, 'Digoxin', 'Perindopril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410383, 'Digoxin', 'Phenylpropanolamine', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 1767317476, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410384, 'Digoxin', 'Phenytoin', 'Moderate', 'Limited clinical data suggest that hydantoins may reduce digoxin serum levels. The mechanism of this interaction is unknown. Although phenytoin has been used in the treatment of digitalis-induced arrhythmias, it has been associated with cardiac arrest in patients with marked heart block.', 'DDInter', 1767317476, 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended. Occasionally, increases in the digoxin dosage may be necessary. The clinician should be aware that the risk of digoxin toxicity may be increased when a hydantoin is discontinued and should consider monitoring the patient more closely for those effects. Phenytoin should be avoided in patients with marked digoxin-induced heart block or bradycardia.', 'Others', 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410385, 'Digoxin', 'Pindolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410386, 'Digoxin', 'Pipecuronium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410387, 'Digoxin', 'Piroxicam', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410388, 'Digoxin', 'Pitolisant', 'Moderate', 'Coadministration with pitolisant may reduce plasma concentrations of substrates of CYP450 2C, P-glycoprotein (P-gp), and uridine diphosphate glucuronosyltransferases (UGTs). Based on in vitro data, pitolisant and its main metabolites may induce CYP450 2C, P-gp, and UGTs.', 'DDInter', 1767317476, 'Caution is recommended if pitolisant is used in combination with substrates of CYP450 2C, P-gp, and/or UGTs. Clinical and laboratory monitoring may be appropriate for some drugs whenever pitolisant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is recommended if pitolisant is used in combination with substrates of CYP450 2C, P-gp, and/or UGTs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410389, 'Digoxin', 'Polythiazide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410390, 'Digoxin', 'Ponatinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with ponatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ponatinib.', 'DDInter', 1767317476, 'Caution is advised when ponatinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ponatinib is added to or withdrawn from therapy.', 'Others', 'Caution is advised when ponatinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410391, 'Digoxin', 'Posaconazole', 'Moderate', 'Theoretically, posaconazole may increase the plasma concentrations and pharmacologic effects of digoxin. The exact mechanism of the interaction is unknown but may be related to azole-induced inhibition of the P-glycoprotein transport of digoxin, resulting in reduced renal clearance.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used concurrently with posaconazole. Serum digoxin levels should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of posaconazole in patients who are stabilized on their digoxin regimen. Patients should be advised to notify their doctor if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used concurrently with posaconazole.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410392, 'Digoxin', 'Prednisolone', 'Moderate', 'Systemically administered corticosteroids may induce hypokalemia and possibly increase the risk of digoxin toxicity. In addition, corticosteroid-induced sodium and water retention can result in edema leading to heart failure. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) which have greater mineralocorticoid activity.', 'DDInter', 1767317476, 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', 'Others', 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
