INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410394, 'Digoxin', 'Procarbazine', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', 'DDInter', 1767317476, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410395, 'Digoxin', 'Procyclidine', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410396, 'Digoxin', 'Propafenone', 'Moderate', 'Propafenone may increase digoxin serum levels. Toxicity may result. The mechanism may be related to changes in the volume of distribution and in the renal and nonrenal clearance of digoxin.', 'DDInter', 1767317476, 'If these drugs must be used together, the patient should be monitored closely for evidence of digoxin toxicity. Patients should be advised to notify their physicians if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'If these drugs must be used together, the patient should be monitored closely for evidence of digoxin toxicity.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410397, 'Digoxin', 'Propantheline', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410398, 'Digoxin', 'Propranolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410399, 'Digoxin', 'Propylthiouracil', 'Moderate', 'The clearance of digitalis glycosides may be reduced when a euthyroid state is achieved after the addition of antithyroid agents.', 'DDInter', 1767317476, 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when antithyroid agents are added, discontinued, or dosing changes are made. Digitalis dose reductions may be required.', 'Excretion', 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when antithyroid agents are added, discontinued, or dosing changes are made.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410400, 'Digoxin', 'Quazepam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410401, 'Digoxin', 'Quinapril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410402, 'Digoxin', 'Quinidine', 'Major', 'Quinidine significantly increases serum digoxin levels in more than 90% of patients. The proposed mechanism is quinidine inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used in combination with quinidine. Empiric reduction in digoxin dosing may be appropriate at the initiation of quinidine therapy. Serum digoxin levels and pharmacologic effects should be closely monitored, and the dosage adjusted accordingly. Patients should be advised to notify their physician if they experience signs of digitalis toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with quinidine.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410403, 'Digoxin', 'Quinine', 'Moderate', 'High doses of quinine (greater than 600 mg/day) may decrease the plasma clearance of digoxin in some patients. Serum digoxin levels and risk of toxicity may be increased. The mechanism is believed to be decreased biliary clearance of digoxin.', 'DDInter', 1767317476, 'During coadministration, the patient should be monitored for signs and symptoms of digoxin toxicity and serum digoxin levels should be checked when clinically necessary. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Metabolism', 'During coadministration, the patient should be monitored for signs and symptoms of digoxin toxicity and serum digoxin levels should be checked when clinically necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410404, 'Digoxin', 'Racepinephrine', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 1767317476, 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410405, 'Digoxin', 'Ramipril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410406, 'Digoxin', 'Ranolazine', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of digoxin. The mechanism is ranolazine inhibition of the P-glycoprotein-mediated efflux of digoxin in the intestine.', 'DDInter', 1767317476, 'Digoxin dosage may need adjustment during coadministration with ranolazine. Pharmacologic response and serum digoxin levels should be monitored more closely whenever ranolazine is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience signs and symptoms of digoxin toxicity such as irregular heartbeats, slow pulse, nausea, anorexia, or visual changes.', 'Absorption', 'Digoxin dosage may need adjustment during coadministration with ranolazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410407, 'Digoxin', 'Rapacuronium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410408, 'Digoxin', 'Remimazolam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410409, 'Digoxin', 'Reserpine', 'Minor', 'Limited data suggest that rauwolfia alkaloids may increase the risk of arrhythmias when they are given with digoxin. The mechanism is not known. This combination has been commonly used without incident. Data are available for reserpine only. Recommended management consists of decreasing the rauwolfia alkaloid dosage.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410410, 'Digoxin', 'Rifampicin', 'Moderate', 'Coadministration with rifampin may decrease the plasma concentrations of digoxin. The proposed mechanism is induction of the P-glycoprotein-mediated intestinal efflux and renal tubular secretion of digoxin by rifampin.', 'DDInter', 1767317476, 'Caution is advised if digoxin is used in combination with rifampin. Digoxin levels should be checked more frequently and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rifampin in patients who are stabilized on their digoxin regimen. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'Excretion', 'Caution is advised if digoxin is used in combination with rifampin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410411, 'Digoxin', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is reduced clearance of digoxin due to ritonavir inhibition of p-glycoprotein-mediated renal tubular secretion.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used in combination with ritonavir. Pharmacologic response and serum digoxin levels should be monitored more closely whenever ritonavir is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with ritonavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410412, 'Digoxin', 'Rivastigmine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 1767317476, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410413, 'Digoxin', 'Rolapitant', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410414, 'Digoxin', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', 'DDInter', 1767317476, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410415, 'Digoxin', 'Ruxolitinib', 'Moderate', 'Coadministration with ruxolitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) in the intestine. The mechanism is decreased clearance via these routes due to inhibition by ruxolitinib.', 'DDInter', 1767317476, 'Caution is advised if ruxolitinib must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) , particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ruxolitinib is added to or withdrawn from therapy. The interaction may possibly be avoided or minimized if the doses of the affected medications are separated for as long as possible.', 'Excretion', 'Caution is advised if ruxolitinib must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) , particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410416, 'Digoxin', 'Salsalate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410417, 'Digoxin', 'Saquinavir', 'Major', 'Saquinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, digoxin, lacosamide, mefloquine, verapamil) may result in elevated risk of conduction disturbances and atrioventricular block. Coadministration with saquinavir may significantly increase the serum concentrations of digoxin. The mechanism of interaction has not been described but may be related to saquinavir inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Ritonavir-boosted saquinavir should generally not be used with other agents that prolong the PR interval. Extreme caution is advised if digoxin must be used concurrently with saquinavir. Serum digoxin levels should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of saquinavir in patients who are stabilized on their digoxin regimen. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Synergism, Excretion', 'Ritonavir-boosted saquinavir should generally not be used with other agents that prolong the PR interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410418, 'Digoxin', 'Sarecycline', 'Moderate', 'Coadministration with sarecycline may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by sarecycline.', 'DDInter', 1767317476, 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sarecycline is added to or withdrawn from therapy.', 'Others', 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410419, 'Digoxin', 'Scopolamine', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410420, 'Digoxin', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may increase the serum concentrations of digoxin. The proposed mechanism is simeprevir inhibition of the P-glycoprotein-mediated intestinal efflux or renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of simeprevir. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of simeprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410421, 'Digoxin', 'Siponimod', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of siponimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis.', 'DDInter', 1767317476, 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. Treatment with siponimod should generally not be initiated in patients who are concurrently treated with heart rate-lowering drugs. Advice from a cardiologist should be sought if coadministration of siponimod and drugs that slow heart rate or AV conduction is considered.', 'Synergism', 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410422, 'Digoxin', 'Sitagliptin', 'Minor', 'Coadministration with sitagliptin does not appear to significantly alter the pharmacokinetics of digoxin. Following administration of digoxin 0.25 mg concomitantly with sitagliptin 100 mg daily for 10 days, the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of digoxin were increased by 18% and 11%, respectively.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410423, 'Digoxin', 'Sodium bicarbonate', 'Minor', 'Concurrent administration of antacids may decrease the oral bioavailability of digoxin and digitoxin. When digoxin and digitoxin are coadministered with Suprep Bowel Prep (magnesium/potassium/sodium sulfates), the manufacturer recommends administering digoxin or digitoxin at least 2 hours before and not less than 6 hours after Suprep Bowel Prep to avoid chelation with magnesium.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410424, 'Digoxin', 'Tolevamer', 'Moderate', 'Cation exchange resins may increase the risk of precipitating hypokalemia and cardiotoxic effects when administered to a patient also receiving cardiac glycosides. The mechanism is the pharmacologic action of exchange resins (increased potassium excretion).', 'DDInter', 1767317476, 'Serum potassium levels should be closely monitored if these agents must be coadministered.', 'Synergism', 'Serum potassium levels should be closely monitored if these agents must be coadministered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410425, 'Digoxin', 'Salicylic acid (sodium)', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410426, 'Digoxin', 'Solifenacin', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410427, 'Digoxin', 'Sorafenib', 'Moderate', 'Based on in vitro inhibition data, coadministration with sorafenib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by sorafenib.', 'DDInter', 1767317476, 'Caution is advised when sorafenib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sorafenib is added to or withdrawn from therapy.', 'Others', 'Caution is advised when sorafenib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410428, 'Digoxin', 'Sotalol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410429, 'Digoxin', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may decrease the plasma concentrations of digoxin. The proposed mechanism is induction of the P-glycoprotein-mediated intestinal efflux and renal tubular secretion of digoxin by constituents of St. John''s wort.', 'DDInter', 1767317476, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. If concomitant use of St. John''s wort is required, digoxin levels should be checked more frequently and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of St. John''s wort in patients who are stabilized on their digoxin regimen. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms. Due to differences in phytochemical content and potency of commercial herbal products in general, the pharmacokinetic effects of St. John''s wort may vary depending on the specific preparation.', 'Absorption, Excretion', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410430, 'Digoxin', 'Succinylcholine', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410431, 'Digoxin', 'Sucralfate', 'Moderate', 'INTERVAL: Sucralfate may reduce the therapeutic effect of digitalis glycosides by interfering with their absorption.', 'DDInter', 1767317476, 'Digitalis should be administered at least two hours before or 6 hours after sucralfate. Patients should be advised to notify their doctor if they experience worsening of their heart symptoms.', 'Absorption', 'Digitalis should be administered at least two hours before or 6 hours after sucralfate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410432, 'Digoxin', 'Sulfasalazine', 'Moderate', 'INTERVAL: Sulfasalazine may decrease the gastrointestinal absorption of digoxin by an unknown mechanism. Bioavailability, serum levels, and effectiveness of digoxin may be decreased significantly.', 'DDInter', 1767317476, 'During coadministration, digoxin effectiveness and serum levels should be monitored. The clinician should consider alternating dosage schedules before resorting to an increase in digoxin dosage. Patients should be advised to notify their physician if they experience worsening of their heart symptoms.', 'Absorption', 'During coadministration, digoxin effectiveness and serum levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410433, 'Digoxin', 'Sulindac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410434, 'Digoxin', 'Sunitinib', 'Moderate', 'There is clinical evidence that sunitinib can prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, lopinavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317476, 'Caution is advised if sunitinib is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block; second-degree or higher AV block; sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. Patients should be advised to notify their doctor if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if sunitinib is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410435, 'Digoxin', 'Suvorexant', 'Moderate', 'Coadministration with suvorexant has been shown to modestly increase the serum concentrations of digoxin. The proposed mechanism is suvorexant inhibition of the P-glycoprotein-mediated intestinal efflux of digoxin.', 'DDInter', 1767317476, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of suvorexant. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of suvorexant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410436, 'Digoxin', 'Tacrine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 1767317476, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410437, 'Digoxin', 'Tegaserod', 'Minor', 'Tegaserod slightly decreased digoxin plasma concentrations and AUC in a clinical study (n=12); however the changes were considered clinically insignificant. The mechanism is unknown. No action is necessary.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410438, 'Digoxin', 'Telaprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is reduced clearance of digoxin due to inhibition of P-glycoprotein-mediated renal tubular secretion.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used in combination with boceprevir or telaprevir. Pharmacologic response and serum digoxin levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with boceprevir or telaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410439, 'Digoxin', 'Telithromycin', 'Moderate', 'Coadministration with telithromycin may increase the plasma concentrations of digoxin. The proposed mechanism is telithromycin inhibition of P-glycoprotein efflux pump in the intestine and renal proximal tubules, resulting in increased absorption and decreased excretion of digoxin.', 'DDInter', 1767317476, 'Pharmacologic response and serum digoxin levels should be monitored more closely whenever telithromycin is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience signs and symptoms of digoxin toxicity such as irregular heartbeats, slow pulse, nausea, anorexia, or visual changes.', 'Absorption, Excretion', 'Pharmacologic response and serum digoxin levels should be monitored more closely whenever telithromycin is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410440, 'Digoxin', 'Telmisartan', 'Moderate', 'Coadministration with telmisartan may modestly increase the serum concentrations of digoxin. The exact mechanism of interaction is unknown. These changes are not thought to be clinically significant, although patients with serum digoxin levels at the upper end of the therapeutic range may be at increased risk for digoxin toxicity. Digoxin did not affect the steady-state pharmacokinetics of telmisartan, and coadministration had no effect on the safety and tolerability of either drug.', 'DDInter', 1767317476, 'Pharmacologic response and serum digoxin levels should be monitored more closely following initiation, discontinuation, or change of dosage of telmisartan in patients who are stabilized on their digoxin regimen. Patients should be advised to contact their physician if they experience signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Others', 'Pharmacologic response and serum digoxin levels should be monitored more closely following initiation, discontinuation, or change of dosage of telmisartan in patients who are stabilized on their digoxin regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410441, 'Digoxin', 'Temazepam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410442, 'Digoxin', 'Teriparatide', 'Moderate', 'Concomitant use of digitalis preparations with human parathyroid hormone analogs may theoretically potentiate the risk of digitalis toxicity and arrhythmias.', 'DDInter', 1767317476, 'Caution is recommended during concomitant use of digitalis and human parathyroid hormone analogs. Serum calcium and digitalis levels should be closely monitored, and the digitalis dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Synergism', 'Caution is recommended during concomitant use of digitalis and human parathyroid hormone analogs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410443, 'Digoxin', 'Thalidomide', 'Moderate', 'Thalidomide can cause bradycardia and may have additive effects with other drugs that decrease heart rate. Cases of bradycardia have been associated with thalidomide use, some requiring medical interventions. The clinical significance and underlying etiology of the bradycardia observed with thalidomide treatment have not been established. However, the potential for additive bradycardic effects should be considered when used with other drugs that slow cardiac conduction such as beta-blockers, calcium channel blockers, digitalis, flecainide, moricizine, and propafenone. Non-cardiac drugs that may also cause bradycardia include atazanavir, lacosamide, lithium, mefloquine, succinylcholine, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Because bradycardia is a risk factor for torsade de pointes arrhythmia, concerns also exist when thalidomide is used with drugs that prolong the QT interval or cause torsade de pointes.', 'DDInter', 1767317476, 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat. A dose reduction of thalidomide or discontinuation may be required.', 'Synergism', 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410444, 'Digoxin', 'Thallous Chloride', 'Moderate', 'Coadministration of certain drugs may alter the myocardial uptake of thallous chloride Tl-201 and interfere with results of myocardial imaging studies. Drugs that increase or decrease coronary blood flow or potassium uptake may alter the biodistribution of thallous chloride Tl-201.', 'DDInter', 1767317476, 'Clinicians should be aware of possible diagnostic interference by these drugs.', 'Others', 'Clinicians should be aware of possible diagnostic interference by these drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410445, 'Digoxin', 'Thyroid, porcine', 'Moderate', 'The clearance of or sensitivity to digitalis glycosides may be increased in previously hypothyroid patients when a euthyroid state is achieved after the addition of thyroid hormones.', 'DDInter', 1767317476, 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when thyroid hormones are added, discontinued, or dosing changes are made. Digitalis dose increases may be required.', 'Metabolism', 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when thyroid hormones are added, discontinued, or dosing changes are made.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410446, 'Digoxin', 'Ticagrelor', 'Moderate', 'Coadministration with ticagrelor may increase the plasma concentrations of digoxin. The proposed mechanism is reduced clearance of digoxin due to inhibition of P-glycoprotein-mediated renal tubular secretion by ticagrelor. Ventricular pauses and bradycardia, mostly asymptomatic, have been observed in clinical studies with ticagrelor. Theoretically, the risk of symptomatic bradycardia may be increased during concomitant use of other agents known to induce bradycardia such as digoxin.', 'DDInter', 1767317476, 'Caution is advised if digoxin must be used in combination with ticagrelor. Pharmacologic response and serum digoxin levels should be monitored more closely whenever ticagrelor is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their doctor if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Synergism, Excretion', 'Caution is advised if digoxin must be used in combination with ticagrelor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410447, 'Digoxin', 'Ticlopidine', 'Minor', 'The manufacturer of ticlopidine has reported a 15% decrease in digoxin serum concentrations in patients receiving ticlopidine. The clinical significance of this interaction is unknown. Digoxin serum concentrations and efficacy should be monitored during concomitant administration. Patients should be advised to notify their physician if they experience worsening of their heart symptoms.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410448, 'Digoxin', 'Tigecycline', 'Minor', 'Tigecycline does not significantly affect the pharmacokinetics of digoxin, and vice versa. Digoxin did not affect the pharmacokinetics of tigecycline. No dosage adjustment of either drug is necessary during coadministration.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410449, 'Digoxin', 'Timolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 1767317476, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410450, 'Digoxin', 'Tofacitinib', 'Moderate', 'Tofacitinib has been shown to decrease heart rate and prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta-blockers, digoxin, alpha-2 adrenoceptor agonists) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317476, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible. Caution is advised if concomitant use is necessary, particularly in patients with known conduction problems or severe cardiac disease. An ECG should be considered in these patients before initiating concomitant therapy and after titration to steady-state. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeats.', 'Synergism', 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410451, 'Digoxin', 'Tolmetin', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 1767317476, 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410452, 'Digoxin', 'Tolterodine', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410453, 'Digoxin', 'Tolvaptan', 'Moderate', 'Coadministration with tolvaptan may increase the serum concentrations of digoxin. The proposed mechanism is decreased clearance due to tolvaptan inhibition of the P-glycoprotein-mediated intestinal and/or renal efflux of digoxin. Digoxin had no significant effect on the pharmacokinetics of tolvaptan, which is also a P-gp substrate.', 'DDInter', 1767317476, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of tolvaptan, and the digoxin dosage adjusted as needed. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of tolvaptan, and the digoxin dosage adjusted as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410454, 'Digoxin', 'Topiramate', 'Minor', 'In a single-dose study, the serum digoxin area under the curve was decreased by 12% when coadministered with topiramate. The mechanism and clinical significance of this interaction is unknown. Serum digoxin levels and clinical response should be monitored. Patients should be advised to notify their physician if they experience worsening heart symptoms.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410455, 'Digoxin', 'Torasemide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410456, 'Digoxin', 'Trandolapril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 1767317476, 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410457, 'Digoxin', 'Trazodone', 'Minor', 'Trazodone may increase serum digoxin levels. The mechanism is unknown and it is not known. If trazodone and digoxin must be used together, close observation for clinical and laboratory evidence of digoxin toxicity is recommended. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410458, 'Digoxin', 'Triamterene', 'Minor', 'Triamterene may decrease the extrarenal clearance of digoxin. Elevated serum digoxin levels may result. The clinical significance of this interaction is expected to be minor. Clinical response and digoxin levels should be monitored during coadministration. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410459, 'Digoxin', 'Triazolam', 'Moderate', 'Benzodiazepines may increase serum levels of digoxin. The mechanism is unknown, but may be related to alterations of digoxin protein binding.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'Close observation for clinical and laboratory evidence of digoxin toxicity is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410460, 'Digoxin', 'Trichlormethiazide', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 1767317476, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410461, 'Digoxin', 'Trihexyphenidyl', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of some digoxin formulations. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, and irregular heartbeats.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410462, 'Digoxin', 'Trimethoprim', 'Minor', 'Trimethoprim may decrease the renal tubular secretion of digoxin. Serum digoxin levels may be increased. The patient should be monitored for digoxin toxicity, and digoxin dosage should be reduced as necessary. Serum digoxin levels usually do not need to be checked unless toxicity is suspected. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410463, 'Digoxin', 'Troleandomycin', 'Moderate', 'Macrolide antibiotics that inhibit P-glycoprotein may increase the plasma concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin. The interaction has been described in numerous case reports in the medical literature, the majority of which involved clarithromycin, a potent P-glycoprotein inhibitor. Some patients have shown clinical signs consistent with digoxin toxicity, including potentially fatal arrhythmias. Exposure to macrolides, specifically clarithromycin, azithromycin and erythromycin, has been identified as a risk factor for digoxin toxicity.', 'DDInter', 1767317476, 'Caution is advised when digoxin or digitoxin is used in combination with macrolide antibiotics that are inhibitors of P-glycoprotein. Serum digitalis levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the macrolide in patients who are stabilized on their digitalis regimen. Patients should be advised to notify their physician if they experience signs of digitalis toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Absorption, Excretion', 'Caution is advised when digoxin or digitoxin is used in combination with macrolide antibiotics that are inhibitors of P-glycoprotein.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410464, 'Digoxin', 'Trospium', 'Moderate', 'Theoretically, coadministration of trospium chloride with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of trospium and/or the coadministered drug(s). The mechanism is competitive inhibition of renal excretion. Drugs that are thought to undergo active tubular secretion include acyclovir/valacyclovir, cidofovir, cimetidine, digoxin, flecainide, ganciclovir/valganciclovir, metformin, midodrine, morphine, pancuronium, procainamide, quinidine, ranitidine, tenofovir, triamterene, and vancomycin.', 'DDInter', 1767317476, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410465, 'Digoxin', 'Tubocurarine', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410466, 'Digoxin', 'Tucatinib', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The mechanism likely involves tucatinib-mediated inhibition of P-gp. Increased exposure to the P-gp substrate may increase the risk of toxicity.', 'DDInter', 1767317476, 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index. A dose reduction of the P-gp substrates as well as clinical and laboratory monitoring may be appropriate.', 'Absorption', 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410467, 'Digoxin', 'Ulipristal', 'Moderate', 'Based on in vitro inhibition data, coadministration with ulipristal may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ulipristal.', 'DDInter', 1767317476, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ulipristal is added to or withdrawn from therapy.', 'Others', 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410468, 'Digoxin', 'Valbenazine', 'Moderate', 'Coadministration with valbenazine may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is decreased clearance due to valbenazine-mediated inhibition of the intestinal P-glycoprotein (P-gp) efflux transport protein.', 'DDInter', 1767317476, 'Caution is advised if valbenazine must be used in combination with digoxin. Pharmacologic response and serum digoxin levels should be monitored more closely whenever valbenazine is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if valbenazine must be used in combination with digoxin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410469, 'Digoxin', 'Vandetanib', 'Moderate', 'Coadministration with vandetanib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein transporter, such as digoxin and dabigatran. The mechanism involves increased absorption and/or decreased clearance due to inhibition of drug efflux mediated by intestinal and renal/hepatic P-glycoprotein, respectively. Vandetanib is a weak P-glycoprotein inhibitor.', 'DDInter', 1767317476, 'Caution is advised if vandetanib must be used concomitantly with medications that are substrates of P-glycoprotein, particularly those with a narrow therapeutic range such as digoxin. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vandetanib is added to or withdrawn from therapy.', 'Absorption, Excretion', 'Caution is advised if vandetanib must be used concomitantly with medications that are substrates of P-glycoprotein, particularly those with a narrow therapeutic range such as digoxin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410470, 'Digoxin', 'Vecuronium', 'Moderate', 'Limited data indicate that the risk of new cardiac arrhythmias is greater when digoxin is given with some depolarizing and nondepolarizing muscle relaxants than when the muscle relaxants are given alone.', 'DDInter', 1767317476, 'Continuous cardiac monitoring is recommended during coadministration.', 'Others', 'Continuous cardiac monitoring is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410471, 'Digoxin', 'Vemurafenib', 'Moderate', 'Coadministration with vemurafenib may increase the serum concentrations of digoxin. The proposed mechanism is vemurafenib inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 1767317476, 'Concomitant use of digoxin with vemurafenib should generally be avoided. If coadministration is required, a dosage reduction of digoxin should be considered. Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of vemurafenib, and the digoxin dosage adjusted as needed. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Concomitant use of digoxin with vemurafenib should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410472, 'Digoxin', 'Venetoclax', 'Moderate', 'Coadministration with venetoclax may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by venetoclax.', 'DDInter', 1767317476, 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. The manufacturer recommends that drugs sensitive to P-gp inhibition in the gastrointestinal tract be administered at least 6 hours before venetoclax. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of venetoclax.', 'Others', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410473, 'Digoxin', 'Vibegron', 'Moderate', 'Coadministration with vibegron may increase the serum concentrations of digoxin. The exact mechanism for this interaction has not been established. However, since both agents are substrates for the P-glycoprotein efflux transporter, competitive inhibition is possible.', 'DDInter', 1767317476, 'Serum digoxin levels should be obtained prior to initiating therapy with vibegron. Monitor pharmacologic response and serum digoxin levels during therapy and after discontinuation of vibegron. Adjust digoxin dosage as needed to attain desired clinical effect.', 'Others', 'Serum digoxin levels should be obtained prior to initiating therapy with vibegron.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410474, 'Digoxin', 'Vincristine', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', 'DDInter', 1767317476, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410475, 'Digoxin', 'Vincristine (liposome)', 'Moderate', 'The use of some antineoplastic agents has been associated with decreased gastrointestinal absorption of digoxin. The suspected mechanism is alteration of the gastrointestinal mucosa. The risk of an interaction is expected to be less with digoxin solution in capsules because absorption occurs rapidly in the upper GI tract.', 'DDInter', 1767317476, 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', 'Absorption', 'Digoxin serum levels and effectiveness should be monitored closely following the initiation or discontinuation of antineoplastic agents, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410476, 'Dihydrocodeine', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4, 2C9, 2C19, breast cancer resistance protein (BCRP), uridine diphosphate glucuronosyltransferase (UGT), organic anion transporting polypeptide 1B1 (OATP1B1), and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to apalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever apalutamide is added to or withdrawn from therapy.', 'Metabolism, Absorption', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410477, 'Dihydrocodeine', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 1767317476, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410478, 'Dihydrocodeine', 'Cannabidiol', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410479, 'Dihydrocodeine', 'Carbamazepine', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', 'DDInter', 1767317476, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410480, 'Dihydrocodeine', 'Ceritinib', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9 and/or 3A4. In vitro data suggest that ceritinib may inhibit these isoenzymes at clinical concentrations.', 'DDInter', 1767317476, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include alfentanil, colchicine, cyclosporine, ergot derivatives, fentanyl, lovastatin, simvastatin, oral midazolam, triazolam, phenytoin, pimozide, quinidine, sildenafil, sirolimus, tacrolimus, and warfarin. If coadministration is required, dosage reductions for the CYP450 2C9 or 3A4 substrates should be considered.', 'Metabolism', 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410481, 'Efavirenz', 'Dihydrocodeine', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', 'DDInter', 1767317476, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410482, 'Ethotoin', 'Dihydrocodeine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410483, 'Glycerol phenylbutyrate', 'Dihydrocodeine', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', 'DDInter', 1767317476, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410484, 'Idelalisib', 'Dihydrocodeine', 'Moderate', 'Coadministration with idelalisib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. Idelalisib has been shown to be a potent inhibitor of this isoenzyme.', 'DDInter', 1767317476, 'Use of idelalisib should generally be avoided with drugs that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range (e.g., antiarrhythmics, anticonvulsants, antineoplastics, immunosuppressants) or those that are considered sensitive substrates (e.g., ergot derivatives, statins, oral midazolam, triazolam). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever idelalisib is added to or withdrawn from therapy, if coadministration is required.', 'Metabolism', 'Use of idelalisib should generally be avoided with drugs that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410485, 'Imatinib', 'Dihydrocodeine', 'Moderate', 'Coadministration with imatinib may increase the plasma concentrations of drugs that are substrates of CYP450 2C9, 2D6 and/or 3A4. The mechanism is decreased clearance due to inhibition of these isoenzymes by imatinib.', 'DDInter', 1767317476, 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever imatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410486, 'Dihydrocodeine', 'Lorlatinib', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', 'DDInter', 1767317476, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410487, 'Dihydrocodeine', 'Mephenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410488, 'Dihydrocodeine', 'Methohexital', 'Moderate', 'Narcotic analgesics may reduce the dosage of barbiturate anesthetics needed to induce anesthesia by as much as 40%. Additionally, apnea is more common with this combination.', 'DDInter', 1767317476, 'Respiratory and cardiovascular status should be closely monitored, and anesthetic dosages titrated accordingly.', 'Others', 'Respiratory and cardiovascular status should be closely monitored, and anesthetic dosages titrated accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
