INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410499, 'Amprenavir', 'Dihydroergotamine', 'Major', 'Coadministration with protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of ergot derivatives. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ergotamine and related drugs.', 'DDInter', 1767317476, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with protease inhibitors is considered contraindicated.', 'Metabolism', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with protease inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410500, 'Amyl Nitrite', 'Dihydroergotamine', 'Moderate', 'Ergot alkaloids, by causing vasoconstriction, may theoretically antagonize the effects of nitrates. In addition, nitrates may increase splanchnic blood flow, resulting in decreased first-pass metabolism of dihydroergotamine and increased bioavailability.', 'DDInter', 1767317476, 'This combination should generally be avoided. Ergot alkaloids are considered contraindicated in patients with ischemic heart disease.', 'Antagonism', 'This combination should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
