INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410520, 'Eluxadoline', 'Dihydroergotamine', 'Moderate', 'It is not known whether, and to what extent, eluxadoline may inhibit CYP450 3A4 and may interact with substrates of the isoenzyme. In vitro studies have shown that eluxadoline is not an inhibitor of CYP450 3A4 at clinically relevant systemic concentrations, but they were not adequate to rule out inhibition of CYP450 3A4 in the gut by eluxadoline. Therefore, the potential for eluxadoline to increase plasma concentrations of oral drugs that are metabolized by CYP450 3A4 should be considered.', 'DDInter', 1767317476, 'Caution is advised when eluxadoline is used with sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, and macrolide immunosuppressants.', 'Others', 'Caution is advised when eluxadoline is used with sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges.', NULL, NULL, 'Moderate', 'pharmacodynamic');
