INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410569, 'Dihydroergotamine', 'Levmetamfetamine (nasal)', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 1767317476, 'Concomitant use of dihydroergotamine with other vasoconstrictive agents is considered contraindicated by the manufacturer. Patients treated with dihydroergotamine should be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Concomitant use of dihydroergotamine with other vasoconstrictive agents is considered contraindicated by the manufacturer.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410570, 'Dihydroergotamine', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Several case reports have suggested that beta-blockers may potentiate the vasoconstrictive action of ergot alkaloids. The exact mechanism of interaction is unknown, but may involve blockade of beta-2-mediated (i.e., sympathetic) vasodilatation. In addition, beta-1 blockade reduces cardiac output, which can diminish blood flow and exacerbate ergot-induced vasospasm.', 'DDInter', 1767317476, 'Except for increased awareness of the interaction, no specific intervention appears to be necessary. Patients should be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Except for increased awareness of the interaction, no specific intervention appears to be necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
