INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1410728, 'Diroximel fumarate', 'Dimethyl fumarate', 'Major', 'Coadministration of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with one another may result in elevated plasma concentrations of MMF and increase the risk of adverse effects such as lymphopenia, opportunistic infections (e.g., progressive multifocal leukoencephalopathy (PML), herpes zoster, herpes simplex, West Nile virus, cytomegalovirus, Candida, Aspergillus, Nocardia, Listeria monocytogenes, Mycobacterium tuberculosis), hepatotoxicity, and flushing. Both dimethyl fumarate and diroximel fumarate are metabolized to MMF, the primary active entity in the treatment of multiple sclerosis.', 'DDInter', 1767317476, 'Coadministration of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate with one another is considered contraindicated. According to the prescribing information, treatment with monomethyl fumarate may be initiated the day following discontinuation of dimethyl fumarate or diroximel fumarate. Likewise, diroximel fumarate may be started the day after stopping dimethyl fumarate.', 'Synergism', 'Coadministration of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate with one another is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
