INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145005, 'Nitisinone', 'Lonafarnib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1. Increased exposure to lonafarnib may increase the risk and/or severity of adverse effects such as nausea, vomiting, diarrhea, anorexia, electrolyte disturbances, liver enzyme elevations, myelosuppression, infection, and hypertension.', NULL, 'The manufacturer recommends avoiding concomitant use of lonafarnib with CYP450 2C9 inhibitors.', NULL, 'The manufacturer recommends avoiding concomitant use of lonafarnib with CYP450 2C9 inhibitors. If coadministration is required, patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since the effect of increased exposures on the QT interval is unknown.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145168/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363025);
