INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145156, 'Fosamprenavir', 'Nizatidine', 'Moderate', 'Coadministration with H2-receptor antagonists or proton pump inhibitors may decrease the oral bioavailability of amprenavir from its prodrug, fosamprenavir. It is possible that fosamprenavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', NULL, 'Caution is advised if fosamprenavir is prescribed with H2-receptor antagonists or proton pump inhibitors.', NULL, 'Caution is advised if fosamprenavir is prescribed with H2-receptor antagonists or proton pump inhibitors. Antiretroviral response should be monitored closely during coadministration.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145319/', '[1] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145157, 'Gefitinib', 'Nizatidine', 'Moderate', 'Coadministration with drugs that cause significant, sustained elevations in gastric pH such as H2-receptor antagonists and proton pump inhibitors may decrease the plasma concentrations of gefitinib. The exact mechanism of interaction is unknown.', NULL, 'The possibility of a diminished therapeutic response to gefitinib should be considered during coadministration with H2-receptor antagonists or proton pump inhibitors.', NULL, 'The possibility of a diminished therapeutic response to gefitinib should be considered during coadministration with H2-receptor antagonists or proton pump inhibitors. Pharmacologic response to gefitinib should be monitored more closely whenever one of these agents is added to or withdrawn from therapy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145320/', '[1] "Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767363025);
