INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145168, 'Ozanimod', 'Nizatidine', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', NULL, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145331/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145169, 'Pazopanib', 'Nizatidine', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of pazopanib and reduce its concentrations in plasma. The solubility of pazopanib is pH-dependent, thus an increase in pH may interfere with its absorption.', NULL, 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', NULL, 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. If acid-suppression therapy is required, short-acting antacids should be considered, with dosing separated by several hours from pazopanib dosing. Similarly, when coadministration with an H2-receptor antagonist is required, it has been recommended that pazopanib be taken without food at least 2 hours before or 10 hours after a dose of the H2-receptor antagonist.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145332/', '[1] van Leeuwen RW, van Gelder T, Mathijssen RH, Jansman FG "Drug-drug interactions with tyrosine-kinase inhibitors: a clinical perspective." Lancet Oncol 15 (2014): e315-e326[2] Yu G, Zheng QS, Wang DX, Zhou HH, Li GF "Drug interactions between tyrosine-kinase inhibitors and acid suppressive agents: more than meets the eye." Lancet Oncol 15 (2014): e469-70[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[5] Tan AR, Gibbon DG, Stein MN, et al. "Effects of ketoconazole and esomeprazole on the pharmacokinetics of pazopanib in patients with solid tumors." Cancer Chemother Pharmacol 71 (2013): 1635-43[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145170, 'Pexidartinib', 'Nizatidine', 'Moderate', 'INTERVAL: Coadministration with acid-reducing agents may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption.', NULL, 'If acid-suppression therapy is required, pexidartinib should be administered at least 2 hours before or 10 hours after taking an H2-receptor antagonist or 2 hours before or 2 hours after taking a locally-acting antacid.', NULL, 'If acid-suppression therapy is required, pexidartinib should be administered at least 2 hours before or 10 hours after taking an H2-receptor antagonist or 2 hours before or 2 hours after taking a locally-acting antacid. Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145333/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145171, 'Piroxicam', 'Nizatidine', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145334/', '[1] Said SA, Foda AM "Influence of cimetidine on the pharmacokinetics of piroxicam in rat and man." Arzneimittelforschung 39 (1989): 790-2[2] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[3] Scavone JM, Greenblatt DJ, Matlis R, Harmatz JS "Interaction of oxaprozin with acetaminophen, cimetidine, and ranitidine." Eur J Clin Pharmacol 31 (1986): 371-4', 1767363025);
