INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145173, 'Posaconazole', 'Nizatidine', 'Moderate', 'Coadministration with H2-receptor antagonists may decrease the systemic bioavailability of posaconazole from the oral suspension. The proposed mechanism is reduced solubility and absorption of posaconazole due to an increase in gastric pH induced by these agents.', NULL, 'Concomitant use of posaconazole oral suspension with cimetidine should generally be avoided.', NULL, 'Concomitant use of posaconazole oral suspension with cimetidine should generally be avoided. Until more information is available, this precaution should also be considered for other H2-receptor antagonists, although the manufacturer suggests otherwise. If possible, prescribers may consider suspending the H2 antagonist until completion of posaconazole therapy, or substituting an antifungal agent like fluconazole or voriconazole whose absorption is not affected by stomach pH. Delayed-release tablets of posaconazole may be used with H2 antagonists, as no pharmacokinetic interaction has been demonstrated with ranitidine or other agents affecting gastric pH such as proton pump inhibitors or antacids.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145336/', '[1] Krishna G, Moton A, Ma L, Medlock MM, McLeod J "The pharmacokinetics and absorption of posaconazole oral suspension under various gastric conditions in healthy volunteers." Antimicrob Agents Chemother 53 (2009): 958-66[2] Alffenaar JW, van Assen S, van der Werf TS, Kosterink JG, Uges DR "Omeprazole significantly reduces posaconazole serum trough level." Clin Infect Dis 48 (2009): 839[3] Shields RK, Clancy CJ, Vadn