INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145175, 'Raltegravir', 'Nizatidine', 'Minor', 'Since raltegravir solubility is increased at higher pH, coadministration with agents that elevate gastric pH may theoretically increase the plasma concentrations of raltegravir. However, concurrent use of proton pump inhibitors or H2-receptor antagonists in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with proton pump inhibitors or H2-receptor antagonists.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145338/', '[1] "Product Information. Isentress (raltegravir)." Merck & Company Inc, West Point, PA.', 1767363025);
