INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145432, 'Stiripentol', 'Norfloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2, 2C19, and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways.', NULL, 'Caution is advised when stiripentol is used with CYP450 1A2, 2C19, and/or 3A4 inhibitors.', NULL, 'Caution is advised when stiripentol is used with CYP450 1A2, 2C19, and/or 3A4 inhibitors. Patients should be monitored for potentially increased adverse effects such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behaviour, neutropenia, and thrombocytopenia. Dosage adjustments for stiripentol or alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145595/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: htt