INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145481, 'Zolmitriptan', 'Norfloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of zolmitriptan, which is a substrate of the isoenzyme.', NULL, 'It is recommended that not more than 5 mg of zolmitriptan be taken in a 24-hour period during concomitant use with CYP450 1A2 inhibitors.', NULL, 'It is recommended that not more than 5 mg of zolmitriptan be taken in a 24-hour period during concomitant use with CYP450 1A2 inhibitors. Patients should be advised to notify their doctor promptly if they experience increased sedation, dizziness, paraesthesia, chest pain or tightness, shortness of breath, and/or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145644/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Multum Information Services, Inc. Expert Review Panel"[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363025);
