INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145713, 'Selpercatinib', 'Nortriptyline', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Phenelzine, Duloxetine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Bupropion, More', 'Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145876/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145714, 'Sertraline', 'Nortriptyline', 'Major', 'Coadministration with sertraline may increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is sertraline inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of many antidepressant and psychotropic drugs. Pharmacodynamically, the combination of sertraline (or any other selective serotonin reuptake inhibitor) and a TCA may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors. The syndrome has been reported in a case involving sertraline and amitriptyline.', NULL, 'Caution is advised if sertraline (or other SSRIs) is prescribed with TCAs.', NULL, 'Caution is advised if sertraline (or other SSRIs) is prescribed with TCAs. Pharmacologic response and plasma TCA levels should be monitored more closely whenever sertraline is added to or withdrawn from therapy in patients stabilized on their existing antidepressant regimen, and the TCA dosage adjusted as necessary. Patients should be monitored closely for signs and symptoms of TCA toxicity (e.g., sedation, dry mouth, blurred vision, constipation, urinary retention) and/or excessive serotonergic activity (e.g., CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia).', 'Metabolism, Synergism', 'Viloxazine, Sertraline, Esketamine', 'Nortriptyline', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145877/', '[1] Sternbach H "The serotonin syndrome." Am J Psychiatry 148 (1991): 705-13[2] Corkeron MA "Serotonin syndrome - a potentially fatal complication of antidepressant therapy." Med J Aust 163 (1995): 481-2[3] Popli AP, Baldessarini RJ, Cole JO "Interactions of serotonin reuptake inhibitors with tricyclic antidepressants." Arch Gen Psychiatry 51 (1994): 666-7[4] Barros J, Asnis G "An interaction of sertraline and desipramine." Am J Psychiatry 150 (1993): 1751[5] Ereshefsky L, Riesemman C, Lam YW "Antidepressant drug interactions and the cytochrome P450 system. The role of cytochrome P450 2D6." Clin Pharmacokinet 29(Suppl 1) (1995): 10-8; discussion 18-9[6] Kurtz DL, Bergstrom RF, Goldberg MJ, Cerimele BJ "The effect of sertraline on the pharmacokinetics of desipramine and imipramine." Clin Pharmacol Ther 62 (1997): 145-56[7] Riesenman C "Antidepressant drug interactions and the cytochrome p450 system: a critical appraisal." Pharmacotherapy 15 (1995): s84-99[8] Taylor D "Selective serotonin reuptake inhibitors and tricyclic antidepressants in combination - interactions and therapeutic uses." Br J Psychiatry 167 (1995): 575-80[9] von Moltke LL, Greenblatt DJ, Cotreau-Bibbo MM, Duan SX, Harmatz JS, Shader RI "Inhibition of desipramine hydroxylation in vitro by serotonin-reuptake-inhibitor antidepressants, and by quinidine and ketoconazole: a model system to predict drug interactions in vivo." J Pharmacol Exp Ther 268 (1994): 1278-83[10] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[11] Preskorn SH, Alderman J, Chung M, Harrison W, Messig M, Harris S "Pharmacokinetics of desipramine coadministered with sertraline or fluoxetine." J Clin Psychopharmacol 14 (1994): 90-8[12] Alderman CP, Lee PC "Serotonin syndrome associated with combined sertraline-amitriptyline treatment." Ann Pharmacother 30 (1996): 1499-500[13] Mathew NT, Tietjen GE, Lucker C "Serotonin syndrome complicating migraine pharmacotherapy." Cephalalgia 16 (1996): 323-7[14] Mills KC "Serotonin syndrome: A 