INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145977, 'Typhoid vaccine (inactivated)', 'Ocrelizumab', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.', NULL, 'Ocrelizumab therapy may interfere with the effectiveness of non-live vaccines.', NULL, 'Ocrelizumab therapy may interfere with the effectiveness of non-live vaccines. The immunization status of patients should be reviewed prior to initiating therapy with ocrelizumab and recommended immunizations with non-live vaccines completed at least 2 weeks prior to initiating therapy (some authorities have recommended at least 6 weeks). Annual influenza vaccines are recommended for patients being treated with ocrelizumab. For infants of mothers exposed to ocrelizumab during pregnancy, non-live vaccines may be administered as indicated, however, adequacy of vaccine response should be assessed; this may include a consultation with a qualified specialist or measurement of vaccine-induced response titers to verify that a protective immune response has been mounted.', 'Others', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146140/', '[1] "Product Information. Ocrevus (ocrelizumab)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145978, 'Typhoid vaccine (live)', 'Ocrelizumab', 'Major', 'The administration of live, attenuated virus or bacterial vaccines during immunosuppressant therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Additionally, there is a lack of data on the safety of immunization following discontinuation of ocrelizumab. Ocrelizumab use causes a depletion of B-cells and can be expected to similarly increase the risk of a decreased or suboptimal immunologic vaccine response if immunization occurs before B-cells counts have recovered.', NULL, 'Live virus or bacterial vaccines should not be administered to patients during therapy or after discontinuing ocrelizumab therapy until recovery from B-cell repletion occurs.', NULL, 'Live virus or bacterial vaccines should not be administered to patients during therapy or after discontinuing ocrelizumab therapy until recovery from B-cell repletion occurs. Immunization status should be assessed prior to initiating ocrelizumab and recommended immunization with live or attenuated live vaccines should be completed at least 4 weeks prior to ocrelizumab initiation (some authorities have recommended at least 6 weeks). For infants of mothers exposed to ocrelizumab during pregnancy, live or live-attenuated vaccines should be delayed until B-cell levels have recovered; therefore, measuring CD19 positive B-cell levels prior to vaccination in these infants is recommended.', 'Others', 'Spesolimab, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Daclizumab, Anifrolumab, Deucravacitinib, Ponesimod, Ravulizumab, Voclosporin, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146141/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Ocrevus (ocrelizumab)." Genentech, South San Francisco, CA.', 1767369485);
