INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (145996, 'Copper oxodotreotide Cu-64', 'Octreotide', 'Major', 'INTERVAL: Coadministration of copper Cu 64 dotatate with non-radioactive somatostatin analogs may diminish the diagnostic effects of copper Cu 64 dotatate positron emission tomography (PET) imaging. Somatostatin analogs competitively bind to the same somatostatin receptor as copper Cu 64 dotatate, which may prevent the uptake of copper Cu 64 dotatate into tissues and affect the diagnostic imaging results.', NULL, 'PET imaging with copper Cu 64 dotatate should be performed prior to dosing with somatostatin analogs.', NULL, 'PET imaging with copper Cu 64 dotatate should be performed prior to dosing with somatostatin analogs. For patients receiving short-acting somatostatin analogs, a washout period of 2 days is recommended prior to imaging. For patients receiving long-acting somatostatin analogs, a washout period of 28 days is recommended prior to imaging.', 'Distribution', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146159/', '[1] "Product Information. Detectnet (copper cu 64 dotatate)." Currax Pharmaceuticals LLC, Morristown, NJ.', 1767369485);
