INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411175, 'Diphenhydramine', 'Nilotinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with nilotinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter and/or CYP450 2D6, CYP450 3A4, or UGT1A1 isoenzymes. The mechanism is decreased clearance via these routes due to inhibition by nilotinib.', 'DDInter', 1767317476, 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nilotinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
