INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411217, 'Diphenhydramine', 'Pitolisant', 'Moderate', 'Because pitolisant enhances the activity of histaminergic neurons in the brain, centrally acting histamine H1-receptor antagonists or medicines with H1-receptor antagonist properties (e.g., tri- and tetracyclic antidepressants) may impair the efficacy of pitolisant.', 'DDInter', 1767317476, 'According to the manufacturer, coadministration of pitolisant with centrally acting H1-receptor antagonists or tri- and tetracyclic antidepressants should be avoided.', 'Antagonism', 'According to the manufacturer, coadministration of pitolisant with centrally acting H1-receptor antagonists or tri- and tetracyclic antidepressants should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411218, 'Diphenhydramine', 'Porfimer sodium', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', 'DDInter', 1767317476, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411219, 'Diphenhydramine', 'Potassium chloride', 'Major', 'Concomitant use of agents with anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, the class IA antiarrhythmic disopyramide) may potentiate the risk of upper gastrointestinal injury associated with oral solid formulations of potassium chloride. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby creating a high localized concentration of potassium ions in the region of a dissolving tablet or capsule and increasing the contact time with GI mucosa. Solid formulations of potassium chloride have been associated with upper GI bleeding and small bowel ulceration, stenosis, perforation, and obstruction.', 'DDInter', 1767317476, 'The use of oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects. A liquid formulation of potassium chloride should be considered. Patients prescribed a solid oral formulation should be advised to discontinue potassium therapy and contact their physician if they experience potential symptoms of upper GI injury such as severe vomiting, abdominal pain, distention, and gastrointestinal bleeding.', 'Absorption', 'The use of oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411220, 'Diphenhydramine', 'Pramipexole', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
