INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411263, 'Diphenhydramine', 'Sotalol', 'Moderate', 'The use of higher than recommended dosages of diphenhydramine (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including cardiac arrest, and arrhythmia related to prolongation of the QT interval. Coadministration with class Ia (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents. Additionally, patients should be counseled to not exceed the recommended dosage and frequency or duration of use of diphenhydramine, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
