INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146159, 'Ofloxacin', 'Meclofenamic acid', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146322/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146160, 'Ofloxacin', 'Melatonin', 'Minor', 'Concomitant administration of CYP450 1A1 or 1A2 inhibitors such as estrogens or quinolones may increase the serum concentration of melatonin. The clinical significance is unknown.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146323/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[3] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7[4] Hartter S, Nordmark A, Rose DM, Bertilsson L, Tybring G, Laine K "Effects of caffeine intake on the pharmacokinetics of melatonin, a probe drug for CYP1A2 activity." Br J Clin Pharmacol 56 (2003): 679-682', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146161, 'Ofloxacin', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', NULL, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146324/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Serentil (mesoridazine)" Boehringer-Ingelheim, Ridgefield, CT.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http