INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146198, 'Procainamide', 'Ofloxacin', 'Major', 'Ofloxacin may cause prolongation of the QT interval in some patients. Coadministration with other agents that can prolong the QT interval such as procainamide may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction. Rare cases of torsade de pointes have been reported with ofloxacin alone. In addition, coadministration with ofloxacin may increase the plasma concentrations of procainamide. The proposed mechanism is competitive inhibition of procainamide elimination via active tubular secretion. Alterations in pharmacodynamic effects of procainamide or the pharmacokinetics of the active n-acetyl metabolite of procainamide were not observed.', NULL, 'The use of ofloxacin in combination with class IA antiarrhythmic agents including procainamide should generally be avoided.', NULL, 'The use of ofloxacin in combination with class IA antiarrhythmic agents including procainamide should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146361/', '[1] Martin DE, Shen J, Griener J, Raasch R, Patterson JH, Cascio W "Effects of ofloxacin on the pharmacokinetics and pharmacodynamics of procainamide." J Clin Pharmacol 36 (1996): 85-91[2] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767363025);
