INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411311, 'Diphenhydramine', 'Vemurafenib', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 and/or 2D6 isoenzymes. The mechanism is decreased clearance due to inhibition of CYP450 1A2 and 2D6 activity by vemurafenib. The interaction has been studied with caffeine and dextromethorphan, probe substrates for CYP450 1A2 and 2D6, respectively.', 'DDInter', 1767317476, 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vemurafenib is added to or withdrawn from therapy. Concomitant use of vemurafenib and substrates of CYP450 1A2 and/or 2D6 with a narrow therapeutic index (e.g., antiarrhythmics; theophylline) should be avoided if possible.', 'Metabolism', 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
