INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411471, 'Poliovirus type 1 antigen (formaldehyde inactivated)', 'Ocrelizumab', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition.', 'DDInter', 1767317476, 'Ocrelizumab therapy may interfere with the effectiveness of non-live vaccines. The immunization status of patients should be reviewed prior to initiating therapy with ocrelizumab and recommended immunizations with non-live vaccines completed at least 2 weeks prior to initiating therapy (some authorities have recommended at least 6 weeks). Annual influenza vaccines are recommended for patients being treated with ocrelizumab. For infants of mothers exposed to ocrelizumab during pregnancy, non-live vaccines may be administered as indicated, however, adequacy of vaccine response should be assessed; this may include a consultation with a qualified specialist or measurement of vaccine-induced response titers to verify that a protective immune response has been mounted.', 'Others', 'Ocrelizumab therapy may interfere with the effectiveness of non-live vaccines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
