INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411578, 'Efalizumab', 'Diroximel fumarate', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1411579, 'Efavirenz', 'Diroximel fumarate', 'Moderate', 'Coadministration of efavirenz with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Efavirenz has been associated with hepatotoxicity during postmarketing use.', 'DDInter', 1767317476, 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; other HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of liver function tests should occur before and during treatment, especially in patients with underlying hepatic disease (including hepatitis B or C coinfection) or marked transaminase elevations.', 'Synergism', 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
