INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146974, 'Procarbazine', 'Oliceridine', 'Major', 'Coadministration of narcotic analgesics with monoamine oxidase inhibitors (MAOIs) has been associated with rare reports of anxiety, confusion, hypotension, respiratory depression, cyanosis, and coma. The mechanism of interaction is unknown, but may involve potentiation of central nervous system (CNS) and respiratory depressant effects by MAOIs.', NULL, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with opioids.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147137/', '[1] "Product Information. Tylenol with Codeine (acetaminophen-codeine)." Janssen Pharmaceuticals, Raritan, NJ.[2] Zornberg GL, Hegarty JD "Adverse interaction between propoxyphene and phenelzine." Am J Psychiatry 150 (1993): 1270-1[3] "Product Information. Emsam (selegiline)." Bristol-Myers Squibb, Princeton, NJ.[4] Michaels I, Serrins M, Shier NQ, Barash PG "Anesthesia for cardiac surgery in patients receiving monoamine oxidase inhibitors." Anesth Analg 63 (1984): 1041-4[5] Evans-Prosser CD "The use of pethidine and morphine in the presence of monoamine oxidase inhibitors." Br J Anaesth 40 (1968): 279-82[6] "Product Information. Talacen (acetaminophen-pentazocine)." Sanofi-Synthelabo Inc, New York, NY.[7] "Product Information. Parnate (tranylcypromine)." SmithKline Beecham, Philadelphia, PA.[8] "Product Information. Buprenex (buprenorphine)." Reckitt and Colman Pharmaceutical, Richmond, VA.[9] "Product Information. Nubain (nalbuphine)." Endo Laboratories, Texarkana, TX.[10] "Product Information. Roxicodone (oxycodone)." Roxane Laboratories Inc, Columbus, OH.[11] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[12] "Product Information. MS Contin (morphine)." Purdue Frederick Company, Norwalk, CT.[13] Garbutt JC "Potentiation of propoxyphene by phenelzine." Am J Psychiatry 144 (1987): 251-2[14] "Product Information. Levo-Dromoran (levorphanol)." Roche Laboratories, Nutley, NJ.[15] "Product Information. Nardil (phenelzine)." Parke-Dvis, Morris Plains, NJ.[16] Browne B, Linter S "Monoamine oxidase inhibitors and narcotic analgesics: a critical review of the implications for treatment." Br J Psychiatry 151 (1987): 210-2[17] "Product Information. Xadago (safinamide)." US WorldMeds LLC, Louisville , KY.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146975, 'Procyclidine', 'Oliceridine', 'Moderate', 'Central anticholinergic agents may have additive central nervous system (CNS) effects with cannabinoids, barbiturates, opiates, and alcohol. These agents individually can cause cognitive and psychomotor impairment, drowsiness, and dizziness, thus concomitant use may result in more potent effects. In addition, the potential for abuse may be increased when central anticholinergic agents are combined with these drugs.', NULL, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.', NULL, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147138/', '[1] "Product Information. Artane (trihexyphenidyl)." Lederle Laboratories, Wayne, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146976, 'Promazine', 'Oliceridine', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147139/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363025);
