INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147015, 'Selpercatinib', 'Oliceridine', 'Major', 'Oliceridine can cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Moreover, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'Caution is recommended if oliceridine is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if oliceridine is used in combination with other drugs that can prolong the QT interval. The daily cumulative dose of oliceridine should not exceed 27 mg. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147178/', '[1] "Product Information. Olinvyk (oliceridine)." Trevena Inc, Chesterbrook, PA.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Olinvyk (oliceridine)." Trevena Inc, Chesterbrook, PA.', 1767363025);
